  1→---
  2→import Header from '../components/Header.astro';
  3→import Footer from '../components/Footer.astro';
  4→import Toast from '../components/islands/Toast.tsx';
  5→import DarkModeToggle from '../components/islands/DarkModeToggle.tsx';
  6→import InstallPrompt from '../components/InstallPrompt.astro';
  7→import { siteConfig } from '../data/site-config';
  8→import '@fontsource-variable/inter';
  9→import interLatinUrl from '@fontsource-variable/inter/files/inter-latin-wght-normal.woff2?url';
 10→import '../styles/global.css';
 11→import '../styles/dark-mode.css';
 12→import '../styles/animations.css';
 13→import '../styles/product-card.css';
 14→
 15→interface Props {
 16→  title: string;
 17→  description?: string;
 18→  includeTurnstile?: boolean;
 19→  preloadImage?: string;
 20→}
 21→
 22→const { title, description = 'Sistemas de Puesta a Tierra y Protección Eléctrica', includeTurnstile = false, preloadImage } = Astro.props;
 23→const canonicalURL = new URL(Astro.url.pathname, Astro.site);
 24→---
 25→
 26→<!DOCTYPE html>
 27→<html lang="es">
 28→<head>
 29→  <meta charset="UTF-8" />
 30→  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
 31→  <meta name="description" content={description} />
 32→  <meta name="author" content="GelChile" />
 33→  <link rel="canonical" href={canonicalURL} />
 34→
 35→  <!-- Open Graph -->
 36→  <meta property="og:title" content={title} />
 37→  <meta property="og:description" content={description} />
 38→  <meta property="og:type" content="website" />
 39→  <meta property="og:locale" content="es_CL" />
 40→  <meta property="og:url" content={canonicalURL} />
 41→  <meta property="og:site_name" content="GelChile" />
 42→  <meta property="og:image" content={`${Astro.site}assets/img/gelchile-logo.webp`} />
 43→  <meta property="og:image:alt" content="GelChile - Sistemas de Puesta a Tierra" />
 44→
 45→  <!-- Twitter Card -->
 46→  <meta name="twitter:card" content="summary" />
 47→  <meta name="twitter:title" content={title} />
 48→  <meta name="twitter:description" content={description} />
 49→  <meta name="twitter:image" content={`${Astro.site}assets/img/gelchile-logo.webp`} />
 50→
 51→  <title>{title}</title>
 52→
 53→  <!-- Favicon -->
 54→  <link rel="icon" type="image/svg+xml" href="/favicon.svg" />
 55→
 56→  <!-- Preload Inter Latin font for faster LCP text rendering -->
 57→  <link rel="preload" as="font" type="font/woff2" href={interLatinUrl} crossorigin />
 58→
 59→  <!-- Preload hero/LCP image when specified -->
 60→  {preloadImage && (
 61→    <link rel="preload" as="image" type="image/webp" href={preloadImage} fetchpriority="high" />
 62→  )}
 63→
 64→  <!-- PWA Manifest -->
 65→  <link rel="manifest" href="/manifest.webmanifest" />
 66→  <meta name="theme-color" content="#0052cc" />
 67→  <meta name="apple-mobile-web-app-capable" content="yes" />
 68→  <meta name="apple-mobile-web-app-status-bar-style" content="black-translucent" />
 69→  <meta name="apple-mobile-web-app-title" content="GelChile" />
 70→  <link rel="apple-touch-icon" href="/icon-192.svg" />
 71→
 72→  <!-- Inter font: self-hosted via @fontsource-variable/inter, preloaded above -->
 73→
 74→  <!-- DNS prefetch for form APIs (low-cost hint for potential navigation to /cotizacion) -->
 75→  <link rel="dns-prefetch" href="https://api.web3forms.com" />
 76→
 77→  <!-- Cloudflare Turnstile (anti-spam) — solo en páginas con formulario -->
 78→  {includeTurnstile && (
 79→    <link rel="preconnect" href="https://challenges.cloudflare.com" crossorigin />
 80→    <script src="https://challenges.cloudflare.com/turnstile/v0/api.js" async defer></script>
 81→  )}
 82→
 83→  <!-- Anti-flash dark mode script -->
 84→  <script is:inline>
 85→    (function() {
 86→      var t = localStorage.getItem('gelchile_theme') ||
 87→        (window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light');
 88→      document.documentElement.setAttribute('data-theme', t);
 89→    })();
 90→  </script>
 91→
 92→  <!-- Structured Data -->
 93→  <script type="application/ld+json" set:html={JSON.stringify({
 94→    "@context": "https://schema.org",
 95→    "@type": "LocalBusiness",
 96→    "name": siteConfig.name,
 97→    "description": siteConfig.description,
 98→    "url": siteConfig.url,
 99→    "telephone": siteConfig.contact.phones[0].replace(/\s/g, ''),
100→    "email": siteConfig.contact.email,
101→    "address": {
102→      "@type": "PostalAddress",
103→      "streetAddress": "Av. Uno #3016",
104→      "addressLocality": "Cerrillos",
105→      "addressRegion": "RM",
106→      "addressCountry": "CL"
107→    }
108→  })} />
109→</head>
110→<body>
111→  <!-- H1: Barra de progreso de navegación -->
112→  <div id="page-progress" class="page-progress"></div>
113→
114→  <a href="#main-content" class="skip-link">Saltar al contenido principal</a>
115→
116→  <Header currentPath={Astro.url.pathname} />
117→
118→  <main id="main-content">
119→    <slot />
120→  </main>
121→
122→  <Footer />
123→
124→  <Toast client:visible />
125→  <DarkModeToggle client:visible />
126→  <InstallPrompt />
127→
128→  <!-- Scroll to Top Button -->
129→  <button id="scroll-top-btn" class="scroll-top-btn" aria-label="Volver arriba">
130→    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" width="24" height="24" aria-hidden="true">
131→      <polyline points="18 15 12 9 6 15"/>
132→    </svg>
133→  </button>
134→
135→  <!-- Consolidated inline scripts: AOS, SW, mobile menu, progress bar, scroll-to-top -->
136→  <script is:inline>
137→    // AOS: Animate On Scroll via IntersectionObserver
138→    requestAnimationFrame(function() {
139→      var els = document.querySelectorAll('[data-aos]');
140→      if (!els.length || !('IntersectionObserver' in window)) {
141→        els.forEach(function(el) { el.classList.add('aos-animate'); });
142→        return;
143→      }
144→      var observer = new IntersectionObserver(function(entries) {
145→        entries.forEach(function(entry) {
146→          if (entry.isIntersecting) {
147→            entry.target.classList.add('aos-animate');
148→            observer.unobserve(entry.target);
149→          }
150→        });
151→      }, { threshold: 0.1 });
152→      els.forEach(function(el) { observer.observe(el); });
153→    });
154→
155→    // Service Worker Registration
156→    if ('serviceWorker' in navigator) {
157→      window.addEventListener('load', function() {
158→        navigator.serviceWorker.register('/sw.js');
159→      });
160→    }
161→
162→    // Mobile Menu
163→    (function() {
164→      var menuBtn = document.querySelector('.mobile-menu-btn');
165→      var menu = document.querySelector('.mobile-menu');
166→      if (!menuBtn || !menu) return;
167→      var isOpen = false;
168→      function openMenu() {
169→        isOpen = true;
170→        menu.classList.add('active');
171→        menuBtn.classList.add('active');
172→        menuBtn.setAttribute('aria-expanded', 'true');
173→        document.body.style.overflow = 'hidden';
174→      }
175→      function closeMenu() {
176→        isOpen = false;
177→        menu.classList.remove('active');
178→        menuBtn.classList.remove('active');
179→        menuBtn.setAttribute('aria-expanded', 'false');
180→        document.body.style.overflow = '';
181→      }
182→      menuBtn.addEventListener('click', function(e) {
183→        e.stopPropagation();
184→        isOpen ? closeMenu() : openMenu();
185→      });
186→      document.addEventListener('click', function(e) {
187→        if (isOpen && !menu.contains(e.target) && !menuBtn.contains(e.target)) closeMenu();
188→      });
189→      document.addEventListener('keydown', function(e) {
190→        if (e.key === 'Escape' && isOpen) closeMenu();
191→      });
192→      menu.querySelectorAll('.nav-link').forEach(function(link) {
193→        link.addEventListener('click', closeMenu);
194→      });
195→    })();
196→
197→    // Page transition progress bar
198→    (function() {
199→      var bar = document.getElementById('page-progress');
200→      if (!bar) return;
201→      document.addEventListener('click', function(e) {
202→        var link = e.target.closest('a');
203→        if (link && link.href && link.href.startsWith(window.location.origin) && !link.hasAttribute('download')) {
204→          bar.style.transform = 'scaleX(0.3)';
205→        }
206→      });
207→      window.addEventListener('load', function() {
208→        bar.style.transform = 'scaleX(1)';
209→        setTimeout(function() { bar.style.transform = 'scaleX(0)'; }, 300);
210→      });
211→    })();
212→
213→    // Scroll to Top
214→    (function() {
215→      var btn = document.getElementById('scroll-top-btn');
216→      if (!btn) return;
217→      var visible = false;
218→      var threshold = 400;
219→      var onScroll = function() {
220→        var show = window.scrollY > threshold;
221→        if (show !== visible) {
222→          visible = show;
223→          btn.classList.toggle('visible', show);
224→        }
225→      };
226→      window.addEventListener('scroll', onScroll, { passive: true });
227→      btn.addEventListener('click', function() {
228→        window.scrollTo({ top: 0, behavior: 'smooth' });
229→      });
230→    })();
231→  </script>
232→
233→  <style is:global>
234→    /* Scroll to Top Button */
235→    .scroll-top-btn {
236→      position: fixed;
237→      bottom: 120px;
238→      right: 24px;
239→      width: 48px;
240→      height: 48px;
241→      border-radius: 50%;
242→      border: none;
243→      background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-dark) 100%);
244→      color: #fff;
245→      cursor: pointer;
246→      display: flex;
247→      align-items: center;
248→      justify-content: center;
249→      box-shadow: 0 4px 14px rgba(0, 82, 204, 0.35);
250→      opacity: 0;
251→      transform: translateY(16px);
252→      pointer-events: none;
253→      transition: opacity 0.3s ease, transform 0.3s ease, background 0.2s ease;
254→      z-index: 50;
255→    }
256→
257→    .scroll-top-btn.visible {
258→      opacity: 1;
259→      transform: translateY(0);
260→      pointer-events: auto;
261→    }
262→
263→    .scroll-top-btn:hover {
264→      background: linear-gradient(135deg, var(--color-primary-dark) 0%, var(--color-primary) 100%);
265→      box-shadow: 0 6px 20px rgba(0, 82, 204, 0.45);
266→    }
267→
268→    .scroll-top-btn:active {
269→      transform: translateY(0) scale(0.95);
270→    }
271→
272→    .scroll-top-btn svg {
273→      width: 24px;
274→      height: 24px;
275→    }
276→
277→    /* Dark mode */
278→    [data-theme="dark"] .scroll-top-btn {
279→      background: linear-gradient(135deg, #1e40af 0%, #1e3a8a 100%);
280→      box-shadow: 0 4px 14px rgba(30, 64, 175, 0.4);
281→    }
282→
283→    [data-theme="dark"] .scroll-top-btn:hover {
284→      background: linear-gradient(135deg, #1e3a8a 0%, #1e40af 100%);
285→      box-shadow: 0 6px 20px rgba(30, 64, 175, 0.5);
286→    }
287→
288→    /* Responsive: avoid overlap with DarkModeToggle */
289→    @media (max-width: 480px) {
290→      .scroll-top-btn {
291→        bottom: 140px;
292→        right: 16px;
293→        width: 44px;
294→        height: 44px;
295→      }
296→    }
297→
298→    /* H1: Page progress bar */
299→    .page-progress {
300→      position: fixed;
301→      top: 0;
302→      left: 0;
303→      height: 3px;
304→      background: linear-gradient(90deg, var(--color-primary-500), var(--color-accent));
305→      width: 100%;
306→      z-index: 9999;
307→      transform: scaleX(0);
308→      transform-origin: left;
309→      transition: transform 0.3s ease;
310→      pointer-events: none;
311→    }
312→  </style>
313→</body>
314→</html>
