  1→import { useState, useMemo, useEffect } from 'preact/hooks';
  2→import { useStore } from '@nanostores/preact';
  3→import { $cart, addItem } from '../../stores/cart';
  4→import { showToast } from '../../stores/toast';
  5→import { $activeVariantGroup } from '../../stores/ui';
  6→import styles from './VariantSelector.module.css';
  7→
  8→/* ---- Types ---- */
  9→
 10→interface Props {
 11→  sku: string;
 12→  title: string;
 13→  category: string;
 14→  image: string | null;
 15→  variants: Array<{ id: string; label: string; group?: string }>;
 16→  onAdd?: () => void;
 17→}
 18→
 19→interface VariantState {
 20→  selected: boolean;
 21→  quantity: number;
 22→}
 23→
 24→type ConfigMode = 'flat' | 'three-step' | 'two-step-bar' | 'two-step-model';
 25→
 26→interface VariantItem { id: string; label: string; group: string; }
 27→interface BarData { size: string; cables: VariantItem[]; }
 28→interface ModelData { code: string; bars?: BarData[]; cables?: VariantItem[]; }
 29→
 30→/* ---- Constants ---- */
 31→
 32→const GAUGE_DOT: Record<string, number> = {
 33→  'Cable N°6': 6,  'Cable N°4': 8,  'Cable N°3': 9,  'Cable N°2': 10,
 34→  'Cable N°1': 12, 'Cable N°1/0': 14, 'Cable N°2/0': 16,
 35→  'Cable N°3/0': 18, 'Cable N°4/0': 20,
 36→  'Cable 250 MCM': 22, 'Cable 500 MCM': 24,
 37→};
 38→
 39→const MODEL_DESC: Record<string, string> = {
 40→  GRT: 'Calibres N°6 a N°2',   GRC: 'Calibres N°2 a 4/0',   GRP: 'Cable N°4 / Barra 3/4"',
 41→  GTC: 'Cable a Varilla en T',  GYE: 'Cable de Paso a Varilla',
 42→  NCR: 'Cable Regular',         NCF: 'Cable Flexible',
 43→  NDR: 'Cable Regular',         NDF: 'Cable Flexible',
 44→  SST: 'Cables N°4 a N°1',     SSC: 'Cables N°1/0 a 4/0',
 45→  VVC: 'Cables N°4, N°2, N°1', VVR: 'Cables N°1/0 a 4/0',
 46→};
 47→
 48→/* ---- Detection & Parsing ---- */
 49→
 50→function detectMode(variants: Props['variants']): ConfigMode {
 51→  if (!variants.some(v => v.group)) return 'flat';
 52→  const groups = [...new Set(variants.map(v => v.group).filter(Boolean))] as string[];
 53→  if (groups.length <= 1) return 'flat';
 54→  const hasSep = groups.some(g => g.includes(' \u2014 '));
 55→  if (hasSep) {
 56→    const models = [...new Set(groups.map(g => { const i = g.indexOf(' \u2014 '); return i > -1 ? g.substring(0, i) : g; }))];
 57→    return models.length === 1 ? 'two-step-bar' : 'three-step';
 58→  }
 59→  return 'two-step-model';
 60→}
 61→
 62→function parseHierarchy(variants: Props['variants'], mode: ConfigMode): ModelData[] {
 63→  if (mode === 'flat') return [];
 64→  const map = new Map<string, ModelData>();
 65→
 66→  if (mode === 'three-step' || mode === 'two-step-bar') {
 67→    for (const v of variants) {
 68→      const g = v.group || '';
 69→      const sep = g.indexOf(' \u2014 ');
 70→      const modelCode = sep > -1 ? g.substring(0, sep) : g;
 71→      const barSize = sep > -1 ? g.substring(sep + 3) : '';
 72→      if (!map.has(modelCode)) map.set(modelCode, { code: modelCode, bars: [] });
 73→      const model = map.get(modelCode)!;
 74→      let bar = model.bars!.find(b => b.size === barSize);
 75→      if (!bar) { bar = { size: barSize, cables: [] }; model.bars!.push(bar); }
 76→      bar.cables.push({ id: v.id, label: v.label, group: g });
 77→    }
 78→  } else {
 79→    for (const v of variants) {
 80→      const code = v.group || '';
 81→      if (!map.has(code)) map.set(code, { code, cables: [] });
 82→      map.get(code)!.cables!.push({ id: v.id, label: v.label, group: code });
 83→    }
 84→  }
 85→
 86→  return [...map.values()];
 87→}
 88→
 89→/* ---- Shared SVG ---- */
 90→
 91→const CartIcon = () => (
 92→  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="18" height="18">
 93→    <path d="M6 2L3 6v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V6l-3-4z" />
 94→    <line x1="3" y1="6" x2="21" y2="6" />
 95→    <path d="M16 10a4 4 0 0 1-8 0" />
 96→  </svg>
 97→);
 98→
 99→/* ============================================
100→   MAIN COMPONENT
101→   ============================================ */
102→
103→export default function VariantSelector({ sku, title, category, image, variants, onAdd }: Props) {
104→  const mode = useMemo(() => detectMode(variants), [variants]);
105→
106→  if (mode === 'flat') {
107→    return <FlatSelector sku={sku} title={title} category={category} image={image} variants={variants} onAdd={onAdd} />;
108→  }
109→
110→  return <StepConfigurator sku={sku} title={title} category={category} image={image} variants={variants} onAdd={onAdd} mode={mode} />;
111→}
112→
113→/* ============================================
114→   FLAT SELECTOR (preserved original behavior)
115→   ============================================ */
116→
117→function FlatSelector({ sku, title, category, image, variants, onAdd }: Props) {
118→  const cart = useStore($cart);
119→
120→  const [state, setState] = useState<Record<string, VariantState>>(() => {
121→    const initial: Record<string, VariantState> = {};
122→    for (const v of variants) initial[v.id] = { selected: false, quantity: 1 };
123→    return initial;
124→  });
125→
126→  const selectedCount = Object.values(state).filter(s => s.selected).length;
127→
128→  const toggleVariant = (id: string) => {
129→    setState(prev => ({ ...prev, [id]: { ...prev[id], selected: !prev[id].selected } }));
130→  };
131→
132→  const setQuantity = (id: string, qty: number) => {
133→    const q = Math.max(1, Math.min(99, qty));
134→    setState(prev => ({ ...prev, [id]: { ...prev[id], quantity: q, selected: true } }));
135→  };
136→
137→  const handleAdd = () => {
138→    const toAdd = variants.filter(v => state[v.id].selected);
139→    if (toAdd.length === 0) return;
140→    for (const v of toAdd) {
141→      addItem({
142→        sku, title, category, quantity: state[v.id].quantity, image,
143→        variant: v.group ? `${v.group} \u2014 ${v.label}` : v.label,
144→      });
145→    }
146→    const msg = toAdd.length === 1
147→      ? `${toAdd[0].label} agregado a la cotización`
148→      : `${toAdd.length} variantes de ${title} agregadas a la cotización`;
149→    showToast(msg, 'success');
150→    setState(prev => {
151→      const reset: Record<string, VariantState> = {};
152→      for (const key of Object.keys(prev)) reset[key] = { selected: false, quantity: 1 };
153→      return reset;
154→    });
155→    onAdd?.();
156→  };
157→
158→  const getCartQty = (v: { label: string; group?: string }): number => {
159→    const variantLabel = v.group ? `${v.group} \u2014 ${v.label}` : v.label;
160→    const item = cart.find(i => i.sku === sku && i.variant === variantLabel);
161→    return item ? item.quantity : 0;
162→  };
163→
164→  /* Grouping logic */
165→  const hasGroups = variants.some(v => v.group);
166→  const subtypes = hasGroups
167→    ? [...new Set(variants.map(v => {
168→        const g = v.group || '';
169→        const idx = g.indexOf(' \u2014 ');
170→        return idx > -1 ? g.substring(0, idx) : g;
171→      }))]
172→    : [];
173→  const uniqueGroups = hasGroups ? [...new Set(variants.map(v => v.group).filter(Boolean))] : [];
174→  const isSingleGroup = uniqueGroups.length === 1;
175→  const grouped = hasGroups && !isSingleGroup
176→    ? variants.reduce<Array<{ group: string; items: typeof variants }>>((acc, v) => {
177→        const g = v.group || '';
178→        const existing = acc.find(entry => entry.group === g);
179→        if (existing) existing.items.push(v);
180→        else acc.push({ group: g, items: [v] });
181→        return acc;
182→      }, [])
183→    : null;
184→
185→  const renderVariantRow = (v: (typeof variants)[0]) => {
186→    const s = state[v.id];
187→    const cartQty = getCartQty(v);
188→    return (
189→      <label key={v.id} class={`${styles.row} ${s.selected ? styles.selected : ''}`}>
190→        <input type="checkbox" class={styles.checkbox} checked={s.selected} onChange={() => toggleVariant(v.id)} />
191→        <span class={styles.label}>
192→          {v.label}
193→          {cartQty > 0 && <span class={styles.inCart}> (en cotización: {cartQty})</span>}
194→        </span>
195→        <div class={styles.qtyControls}>
196→          <button type="button" class={styles.qtyBtn} onClick={(e) => { e.preventDefault(); setQuantity(v.id, s.quantity - 1); }} aria-label="Disminuir cantidad">{'\u2212'}</button>
197→          <input type="number" class={styles.qtyInput} min="1" max="99" value={s.quantity} onInput={(e) => { const val = parseInt((e.target as HTMLInputElement).value); if (!isNaN(val)) setQuantity(v.id, val); }} />
198→          <button type="button" class={styles.qtyBtn} onClick={(e) => { e.preventDefault(); setQuantity(v.id, s.quantity + 1); }} aria-label="Aumentar cantidad">+</button>
199→        </div>
200→      </label>
201→    );
202→  };
203→
204→  return (
205→    <div class={styles.selector}>
206→      {hasGroups && subtypes.length > 0 && (
207→        <div class={styles.modelInfo}>
208→          <span class={styles.modelLabel}>{subtypes.length === 1 ? 'Modelo' : 'Modelos disponibles'}</span>
209→          <span class={styles.modelName}>
210→            {subtypes.map((name, i) => (
211→              <span key={name}>
212→                {i > 0 && <span class={styles.modelSeparator}>/</span>}
213→                {name}
214→              </span>
215→            ))}
216→          </span>
217→        </div>
218→      )}
219→
220→      <div class={styles.selectorHeading}>
221→        <span class={styles.headingText}>Seleccionar variante</span>
222→        <span class={styles.headingCount}>
223→          {selectedCount > 0 ? `${selectedCount} / ${variants.length}` : `${variants.length} opciones`}
224→        </span>
225→      </div>
226→
227→      <div class={styles.list}>
228→        {grouped
229→          ? grouped.map(({ group, items }) => (
230→              <div key={group}>
231→                <div class={styles.groupDivider}>{group}</div>
232→                {items.map(renderVariantRow)}
233→              </div>
234→            ))
235→          : variants.map(renderVariantRow)
236→        }
237→      </div>
238→
239→      <button type="button" class={styles.addBtn} onClick={handleAdd} disabled={selectedCount === 0}>
240→        <CartIcon />
241→        {selectedCount === 0
242→          ? 'Selecciona al menos una variante'
243→          : selectedCount === 1
244→            ? 'Agregar 1 variante a Cotización'
245→            : `Agregar ${selectedCount} variantes a Cotización`}
246→      </button>
247→    </div>
248→  );
249→}
