251→/* ============================================
252→   STEP CONFIGURATOR (multi-step)
253→   ============================================ */
254→
255→function StepConfigurator({ sku, title, category, image, variants, onAdd, mode }: Props & { mode: ConfigMode }) {
256→  const cart = useStore($cart);
257→  const models = useMemo(() => parseHierarchy(variants, mode), [variants, mode]);
258→
259→  const hasBars = mode === 'three-step' || mode === 'two-step-bar';
260→  const hasModelStep = mode === 'three-step' || mode === 'two-step-model';
261→  const isRunTap = mode === 'two-step-model' && models.every(m => m.code.startsWith('Cable '));
262→
263→  const [activeModel, setActiveModel] = useState(models[0]?.code || '');
264→  const [activeBar, setActiveBar] = useState<string | null>(
265→    hasBars ? (models[0]?.bars?.[0]?.size || null) : null
266→  );
267→  const [selected, setSelected] = useState<Record<string, number>>({});
268→
269→  // Emit initial model to gallery on mount
270→  useEffect(() => { $activeVariantGroup.set(models[0]?.code || ''); }, []);
271→
272→  /* Derived data (memoized to avoid recalculating on every render) */
273→  const currentModel = useMemo(() => models.find(m => m.code === activeModel), [models, activeModel]);
274→  const availableBars = useMemo(() => hasBars ? currentModel?.bars : null, [hasBars, currentModel]);
275→  const currentCables = useMemo(() => {
276→    if (hasBars) return currentModel?.bars?.find(b => b.size === activeBar)?.cables;
277→    return currentModel?.cables;
278→  }, [hasBars, currentModel, activeBar]);
279→
280→  const selectedEntries = Object.entries(selected);
281→  const selectedCount = selectedEntries.length;
282→  const totalQty = selectedEntries.reduce((sum, [, qty]) => sum + qty, 0);
283→
284→  /* Handlers */
285→  const handleModelChange = (code: string) => {
286→    setActiveModel(code);
287→    $activeVariantGroup.set(code);
288→    if (hasBars) {
289→      const newModel = models.find(m => m.code === code);
290→      const bars = newModel?.bars || [];
291→      if (!bars.find(b => b.size === activeBar)) {
292→        setActiveBar(bars[0]?.size || null);
293→      }
294→    }
295→  };
296→
297→  const toggleCable = (id: string) => {
298→    setSelected(prev => {
299→      if (id in prev) {
300→        const next = { ...prev };
301→        delete next[id];
302→        return next;
303→      }
304→      return { ...prev, [id]: 1 };
305→    });
306→  };
307→
308→  const setCableQty = (id: string, qty: number) => {
309→    const q = Math.max(1, Math.min(99, qty));
310→    setSelected(prev => ({ ...prev, [id]: q }));
311→  };
312→
313→  const removeSelected = (id: string) => {
314→    setSelected(prev => {
315→      const next = { ...prev };
316→      delete next[id];
317→      return next;
318→    });
319→  };
320→
321→  const clearAll = () => setSelected({});
322→
323→  const handleAdd = () => {
324→    const entries = Object.entries(selected);
325→    if (entries.length === 0) return;
326→    for (const [varId, qty] of entries) {
327→      const v = variants.find(x => x.id === varId);
328→      if (!v) continue;
329→      addItem({
330→        sku, title, category, quantity: qty, image,
331→        variant: v.group ? `${v.group} \u2014 ${v.label}` : v.label,
332→      });
333→    }
334→    showToast(
335→      entries.length === 1
336→        ? '1 variante agregada a la cotización'
337→        : `${entries.length} variantes agregadas a la cotización`,
338→      'success'
339→    );
340→    setSelected({});
341→    onAdd?.();
342→  };
343→
344→  /* Helpers */
345→  const barHasSelections = (barSize: string): boolean => {
346→    const bar = currentModel?.bars?.find(b => b.size === barSize);
347→    return bar?.cables.some(c => c.id in selected) || false;
348→  };
349→
350→  const getChipLabel = (variantId: string): string => {
351→    const v = variants.find(x => x.id === variantId);
352→    if (!v) return variantId;
353→    if (mode === 'two-step-model') {
354→      if (isRunTap) {
355→        const run = v.group?.replace('Cable ', '') || '';
356→        const tap = v.label.replace('Cable ', '');
357→        return `${run} \u2192 ${tap}`;
358→      }
359→      return `${v.group} \u00B7 ${v.label}`;
360→    }
361→    const g = v.group || '';
362→    const sep = g.indexOf(' \u2014 ');
363→    const model = sep > -1 ? g.substring(0, sep) : g;
364→    const param = sep > -1 ? g.substring(sep + 3) : '';
365→    if (mode === 'two-step-bar') return param ? `${param} ${v.label}` : v.label;
366→    return param ? `${model} ${param} ${v.label}` : `${model} \u00B7 ${v.label}`;
367→  };
368→
369→  const getCartQty = (v: { label: string; group?: string }): number => {
370→    const variantLabel = v.group ? `${v.group} \u2014 ${v.label}` : v.label;
371→    return cart.find(i => i.sku === sku && i.variant === variantLabel)?.quantity || 0;
372→  };
373→
374→  /* Step numbering */
375→  const barStepNum = hasModelStep ? 2 : 1;
376→  const cableStepNum = (hasModelStep ? 1 : 0) + (hasBars ? 1 : 0) + 1;
377→
378→  return (
379→    <div class={styles.selector}>
380→      {/* Config header */}
381→      <div class={styles.configHeader}>
382→        <span class={styles.configTitle}>Configurar variante</span>
383→        <span class={styles.configCount}>{variants.length} opciones</span>
384→      </div>
385→
386→      {/* Breadcrumb */}
387→      <div class={styles.breadcrumb}>
388→        {hasModelStep && <span class={styles.breadcrumbChip}>{activeModel}</span>}
389→        {hasModelStep && hasBars && activeBar && (
390→          <>
391→            <span class={styles.breadcrumbArrow}>{'\u203A'}</span>
392→            <span class={styles.breadcrumbChip}>{activeBar}</span>
393→          </>
394→        )}
395→        {!hasModelStep && hasBars && activeBar && (
396→          <span class={styles.breadcrumbChip}>{activeBar}</span>
397→        )}
398→      </div>
399→
400→      {/* 2-step-bar: static model info */}
401→      {mode === 'two-step-bar' && models[0] && (
402→        <div class={styles.modelStatic}>
403→          <span class={styles.modelStaticLabel}>Modelo:</span>
404→          <span class={styles.modelStaticName}>{models[0].code}</span>
405→        </div>
406→      )}
407→
408→      {/* Step: Model selection (3-step, 2-step-model) */}
409→      {hasModelStep && (
410→        <div class={styles.stepSection}>
411→          <div class={styles.stepHeader}>
412→            <span class={styles.stepBadge}>1</span>
413→            <span class={styles.stepLabel}>
414→              {mode === 'three-step' ? 'Tipo de Molde' : isRunTap ? 'Cable Principal' : 'Modelo'}
415→            </span>
416→          </div>
417→          <div class={styles.modelCards}>
418→            {models.map(m => (
419→              <div
420→                key={m.code}
421→                class={m.code === activeModel ? styles.modelCardActive : styles.modelCard}
422→                onClick={() => handleModelChange(m.code)}
423→                role="radio"
424→                aria-checked={m.code === activeModel}
425→              >
426→                <div class={styles.modelRadio}>
427→                  <div class={styles.modelRadioDot} />
428→                </div>
429→                <div class={styles.modelCardText}>
430→                  <span class={styles.modelCardName}>{m.code}</span>
431→                  {MODEL_DESC[m.code] && (
432→                    <span class={styles.modelCardSub}>{MODEL_DESC[m.code]}</span>
433→                  )}
434→                </div>
435→              </div>
436→            ))}
437→          </div>
438→        </div>
439→      )}
440→
441→      {/* Step: Parameter selection (bar/zapata/bus/varilla) */}
442→      {hasBars && availableBars && (availableBars.length > 1 || (availableBars.length === 1 && availableBars[0].size)) && (() => {
443→        const firstWord = availableBars.find(b => b.size)?.size.split(' ')[0] || '';
444→        const stepLabel = firstWord === 'Barra' ? 'Di\u00E1metro de Barra'
445→          : firstWord === 'Zapata' ? 'Zapata'
446→          : firstWord === 'Bus' ? 'Dimensi\u00F3n de Bus'
447→          : firstWord === 'Varilla' ? 'Di\u00E1metro de Varilla'
448→          : 'Configuraci\u00F3n';
449→        return (
450→          <div class={styles.stepSection}>
451→            <div class={styles.stepHeader}>
452→              <span class={styles.stepBadge}>{barStepNum}</span>
453→              <span class={styles.stepLabel}>{stepLabel}</span>
454→            </div>
455→            <div class={styles.barContainer}>
456→              <div class={styles.barPills}>
457→                {availableBars.map(bar => {
458→                  const isActive = bar.size === activeBar;
459→                  const hasSel = !isActive && barHasSelections(bar.size);
460→                  return (
461→                    <button
462→                      key={bar.size}
463→                      type="button"
464→                      class={isActive ? styles.barPillActive : styles.barPill}
465→                      onClick={() => setActiveBar(bar.size)}
466→                      aria-pressed={isActive}
467→                    >
468→                      {bar.size}
469→                      {hasSel && <span class={styles.barDot} />}
470→                    </button>
471→                  );
472→                })}
473→              </div>
474→            </div>
475→          </div>
476→        );
477→      })()}
478→
479→      {/* Step: Cable selection */}
480→      {currentCables && currentCables.length > 0 && (
481→        <div class={styles.stepSection}>
482→          <div class={styles.stepHeader}>
483→            <span class={styles.stepBadge}>{cableStepNum}</span>
484→            <span class={styles.stepLabel}>{isRunTap ? 'Cable Derivaci\u00F3n' : 'Seleccionar Variante'}</span>
485→          </div>
486→          <div class={styles.cableList}>
487→            {currentCables.map(cable => {
488→              const isSelected = cable.id in selected;
489→              const dotSize = GAUGE_DOT[cable.label];
490→              const cartQty = getCartQty(cable);
491→              const dotOuter = dotSize ? dotSize + 10 : 18;
492→              const dotInner = dotSize || 8;
493→
494→              return (
495→                <label
496→                  key={cable.id}
497→                  class={isSelected ? styles.cableRowSelected : styles.cableRow}
498→                >
499→                  <input
500→                    type="checkbox"
501→                    class={styles.cableCheckbox}
502→                    checked={isSelected}
503→                    onChange={() => toggleCable(cable.id)}
504→                  />
505→                  {dotSize && (
506→                    <span
507→                      class={styles.gaugeDot}
508→                      style={{ width: `${dotOuter}px`, height: `${dotOuter}px` }}
509→                    >
510→                      <span
511→                        class={styles.gaugeDotInner}
512→                        style={{ width: `${dotInner}px`, height: `${dotInner}px` }}
513→                      />
514→                    </span>
515→                  )}
516→                  <span class={styles.cableLabel}>
517→                    {cable.label}
518→                    {cartQty > 0 && (
519→                      <span class={styles.cableInCart}> (en cotizaci{'\u00F3'}n: {cartQty})</span>
520→                    )}
521→                  </span>
522→                  {isSelected && (
523→                    <div class={styles.qtyControls}>
524→                      <button
525→                        type="button"
526→                        class={styles.qtyBtn}
527→                        onClick={(e) => { e.preventDefault(); setCableQty(cable.id, selected[cable.id] - 1); }}
528→                        aria-label="Disminuir cantidad"
529→                      >{'\u2212'}</button>
530→                      <input
531→                        type="number"
532→                        class={styles.qtyInput}
533→                        min="1"
534→                        max="99"
535→                        value={selected[cable.id]}
536→                        onInput={(e) => {
537→                          const val = parseInt((e.target as HTMLInputElement).value);
538→                          if (!isNaN(val)) setCableQty(cable.id, val);
539→                        }}
540→                      />
541→                      <button
