 67→<script>
 68→  // Save/restore scroll position so "Volver al Catálogo" returns to the same spot
 69→  const SCROLL_KEY = 'gelchile_catalog_scroll';
 70→
 71→  // Restore on load
 72→  const saved = sessionStorage.getItem(SCROLL_KEY);
 73→  if (saved) {
 74→    const y = parseInt(saved, 10);
 75→    // Wait for content to render before scrolling
 76→    requestAnimationFrame(() => {
 77→      window.scrollTo(0, y);
 78→    });
 79→  }
 80→
 81→  // Save on scroll (debounced — 500ms to reduce main-thread work)
 82→  let scrollTimer: ReturnType<typeof setTimeout>;
 83→  window.addEventListener('scroll', () => {
 84→    clearTimeout(scrollTimer);
 85→    scrollTimer = setTimeout(() => {
 86→      sessionStorage.setItem(SCROLL_KEY, String(window.scrollY));
 87→    }, 500);
 88→  }, { passive: true });
