 1→/**
 2→ * Type definitions for Gel Chile — derived from content.config.ts Zod schema.
 3→ *
 4→ * These types are used in components and pages that consume product data.
 5→ * The canonical source of truth is the Zod schema in src/content.config.ts.
 6→ * After running `astro sync`, Astro also generates CollectionEntry<'products'>
 7→ * which can be used as an alternative.
 8→ */
 9→
10→/** Product category slug — used in URLs and filtering */
11→export type CategorySlug =
12→  | 'electrodos-electromagneticos'
13→  | 'electrodos-grafito'
14→  | 'barras-quimicas'
15→  | 'aditivos'
16→  | 'pararrayos'
17→  | 'soldadura-exotermica'
18→  | 'moldes-grafito'
19→  | 'accesorios'
20→  | 'servicios';
21→
22→/** Product category display name */
23→export type CategoryName =
24→  | 'Electrodos Electromagnéticos'
25→  | 'Electrodos de Grafito'
26→  | 'Barras Químicas'
27→  | 'Aditivos'
28→  | 'Pararrayos y Protección'
29→  | 'Soldadura Exotérmica'
30→  | 'Moldes de Grafito'
31→  | 'Accesorios'
32→  | 'Servicios';
33→
34→/** Product badge values */
35→export type BadgeValue = 'Popular' | 'Pro' | 'Servicio';
36→
37→/** Product data shape — matches content.config.ts schema */
38→export interface Product {
39→  sku: string;
40→  title: string;
41→  category: CategoryName;
42→  categorySlug: CategorySlug;
43→  description: string;
44→  features: string[];
45→  specs: Record<string, string>;
46→  image: string | null;
47→  images?: string[];
48→  inStock: boolean;
49→  badge: BadgeValue | null;
50→  subcategories?: string[];
51→  variants?: Array<{ id: string; label: string; group?: string }>;
52→}
53→
54→/** Slim product data for catalog grid (excludes features, images, variants) */
55→export type ProductSlim = Pick<Product,
56→  'sku' | 'title' | 'category' | 'categorySlug' | 'description' | 'image' | 'badge' | 'inStock' | 'subcategories'
57→> & {
58→  hasVariants: boolean;
59→};
60→
61→/** Category metadata from categories.json */
62→export interface Category {
63→  id: CategorySlug;
64→  name: CategoryName;
65→  description: string;
66→  count: number;
67→}
