  1→#!/usr/bin/env node
  2→/**
  3→ * Copy extracted source images to public/assets/img/products/ with correct names.
  4→ * These will then be re-optimized by optimize-images.mjs at quality 85.
  5→ *
  6→ * Usage: node scripts/copy-mapped-images.mjs
  7→ */
  8→import { copyFile, stat, mkdir } from 'node:fs/promises';
  9→import { join, extname } from 'node:path';
 10→
 11→const ROOT = process.cwd();
 12→const STAGING = join(ROOT, '_image-staging');
 13→const PRODUCTS_DIR = join(ROOT, 'public', 'assets', 'img', 'products');
 14→
 15→/**
 16→ * Mapping: [source path relative to _image-staging, target filename in products dir]
 17→ * Only includes HIGH CONFIDENCE mappings based on DOCX content analysis.
 18→ */
 19→const MAPPINGS = [
 20→  // === Electrode Products (from DOCX image1=product photo, image2=detail) ===
 21→  // HE-45 (ELE-001)
 22→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image1.png', 'electrode-he-45-product.png'],
 23→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image2.png', 'electrode-he-45-detail.png'],
 24→  // HE-70 (ELE-002)
 25→  ['docx/ELECTRODO ELECTROMAGNETICO HE 70  HE/image1.png', 'electrode-he-70-product.png'],
 26→  ['docx/ELECTRODO ELECTROMAGNETICO HE 70  HE/image2.png', 'electrode-he-70-detail.png'],
 27→  // HE-100 (ELE-003)
 28→  ['docx/ELECTRODO ELECTROMAGNETICO HE 100  HE/image1.png', 'electrode-he-100-product.png'],
 29→  ['docx/ELECTRODO ELECTROMAGNETICO HE 100  HE/image2.png', 'electrode-he-100-detail.png'],
 30→  // HE-400 (ELE-005) — image1=product, image2=detail (ele-005-pack is separate packaging photo)
 31→  ['docx/ELECTRODO ELECTROMAGNETICO HE-400  HE 2025/image1.png', 'electrode-he-400-product.png'],
 32→  ['docx/ELECTRODO ELECTROMAGNETICO HE-400  HE 2025/image2.png', 'electrode-he-400-detail.png'],
 33→  // HE-700 (ELE-006)
 34→  ['docx/ELECTRODO ELECTROMAGNETICO HE-700  HE/image1.png', 'electrode-he-700-product.png'],
 35→  ['docx/ELECTRODO ELECTROMAGNETICO HE-700  HE/image2.png', 'electrode-he-700-detail.png'],
 36→  // HE-1500 (ELE-007)
 37→  ['docx/ELECTRODO ELECTROMAGNETICO HE 1500  HE/image1.png', 'electrode-he-1500-product.png'],
 38→  ['docx/ELECTRODO ELECTROMAGNETICO HE 1500  HE/image2.png', 'electrode-he-1500-detail.png'],
 39→  // HE-2500 (ELE-008)
 40→  ['docx/ELECTRODO ELECTROMAGNETICO HE 2500  HE/image1.png', 'electrode-he-2500-product.png'],
 41→  ['docx/ELECTRODO ELECTROMAGNETICO HE 2500  HE/image2.png', 'electrode-he-2500-detail.png'],
 42→
 43→  // === Electrodo de Grafito (EGR-001) — image2 is the actual product photo ===
 44→  ['docx/ELECTRODO DE GRAFITO EG-GELCHIEL/image2.png', 'ele-001.png'],
 45→
 46→  // === Tableros (TAB-001, TAB-002) ===
 47→  ['docx/FICHA TECNICA TABLERO ELECTRICO 300X200X150/image1.png', 'tab-001.png'],
 48→  ['docx/FICHA TECNICA TABLERO ELECTRICO 400X300X200/image1.png', 'tab-002.png'],
 49→
 50→  // === Camarilla PVC (CAM-001) — main image is cam-002 ===
 51→  ['docx/FICHA TECNICA CAMARILLA DE REGISTRO PVC NARANJA/image1.png', 'cam-002.png'],
 52→
 53→  // === Pararrayo (PAR-001) ===
 54→  ['docx/FICHA TECNICA PARARRAYO TIPO FRANKLIN/image1.png', 'par-001.png'],
 55→
