 63→  ['docx/FICHA TECNICA MASILLA/image2.png', 'mas-003.png'],
 64→  ['docx/FICHA TECNICA MASILLA/image3.png', 'mas-002.png'],
 65→  ['docx/FICHA TECNICA MASILLA/image1.png', 'mas-001.png'],
 66→
 67→  // === Chispero (PAR-003) — main product image ===
 68→  ['docx/CERTIFICADO DE CALIDAD CHISPERO/image1.jpeg', 'chi-004.jpeg'],
 69→
 70→  // === Servicios (SRV-002) — WhatsApp photos sorted by timestamp ===
 71→  ['servicios/WhatsApp Image 2026-01-28 at 20.35.43.jpeg', 'srv-malla-01.jpeg'],
 72→  ['servicios/WhatsApp Image 2026-01-28 at 20.38.38.jpeg', 'srv-malla-02.jpeg'],
