 77→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image9.png', 'hunter-energy-branding.png'],
 78→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image7.png', 'hunter-energy-certification-standard.png'],
 79→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image8.png', 'hunter-energy-dimensions-standard.png'],
 80→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image5.png', 'hunter-energy-specs-standard.png'],
 81→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image6.png', 'hunter-energy-icon.png'],
 82→  ['docx/ELECTRODO ELECTROMAGNETICO HE 45  HE/image3.png', 'hunter-energy-logo.png'],
 83→  // HE-400 specific variants
 84→  ['docx/ELECTRODO ELECTROMAGNETICO HE-400  HE 2025/image7.png', 'hunter-energy-certification-he-400.png'],
 85→  ['docx/ELECTRODO ELECTROMAGNETICO HE-400  HE 2025/image5.png', 'hunter-energy-specs-he-400.png'],
 86→  ['docx/ELECTRODO ELECTROMAGNETICO HE-400  HE 2025/image8.png', 'hunter-energy-dimensions-he-400.png'],
 87→  // HE-700 specific variants
 88→  ['docx/ELECTRODO ELECTROMAGNETICO HE-700  HE/image7.png', 'hunter-energy-specs-he-700.png'],
 89→  ['docx/ELECTRODO ELECTROMAGNETICO HE-700  HE/image5.png', 'hunter-energy-dimensions-he-700.png'],
 90→];
 91→
 92→async function main() {
 93→  console.log('=== Copy Mapped Source Images ===\n');
 94→
 95→  let copied = 0;
 96→  let skipped = 0;
 97→  let errors = 0;
 98→
 99→  for (const [srcRel, destName] of MAPPINGS) {
100→    const srcPath = join(STAGING, srcRel);
101→    const destPath = join(PRODUCTS_DIR, destName);
102→
103→    try {
104→      // Check if source exists
105→      await stat(srcPath);
106→
107→      // Remove existing WebP with same base name (will be replaced by optimize-images)
108→      const baseName = destName.replace(extname(destName), '');
109→      const existingWebp = join(PRODUCTS_DIR, baseName + '.webp');
110→
111→      try {
112→        await stat(existingWebp);
113→        // WebP exists, it will be kept alongside the new source file
114→        // optimize-images will process the new PNG/JPG and replace the WebP
115→      } catch {
116→        // No existing WebP, that's fine
117→      }
118→
119→      await copyFile(srcPath, destPath);
120→      const stats = await stat(destPath);
121→      console.log(`  ✓ ${destName} (${(stats.size / 1024).toFixed(0)} KB)`);
122→      copied++;
123→    } catch (err) {
124→      if (err.code === 'ENOENT') {
125→        console.log(`  ✗ SKIP: ${srcRel} (source not found)`);
126→        skipped++;
127→      } else {
128→        console.log(`  ✗ ERROR: ${destName}: ${err.message}`);
129→        errors++;
130→      }
131→    }
132→  }
133→
134→  console.log(`\n=== Summary ===`);
135→  console.log(`Copied: ${copied} images`);
136→  console.log(`Skipped: ${skipped}`);
137→  console.log(`Errors: ${errors}`);
138→  console.log(`\nNext step: run 'npm run optimize-images' to convert to WebP at quality 85`);
139→}
140→
141→main().catch(err => {
142→  console.error('Fatal error:', err);
143→  process.exit(1);
144→});
