  1→/* ============================================
  2→   GEL CHILE - CSS Global
  3→   Variables + Reset + Utilidades + Tipografía + Botones
  4→   Extraído de css/styles.css (líneas 8-369)
  5→   ============================================ */
  6→
  7→/* ============================================
  8→   1. VARIABLES CSS (Design Tokens)
  9→   ============================================ */
 10→:root {
 11→  /* Colores Primarios */
 12→  --color-primary: #0052CC;
 13→  --color-primary-dark: #003D99;
 14→  --color-primary-light: #4C9AFF;
 15→  --color-primary-50: #E6F0FF;
 16→  --color-primary-100: #CCE0FF;
 17→  --color-primary-200: #99C2FF;
 18→  --color-primary-300: #66A3FF;
 19→  --color-primary-400: #3385FF;
 20→  --color-primary-500: #0052CC;
 21→  --color-primary-600: #0043A8;
 22→
 23→  /* Colores Secundarios/Acento */
 24→  --color-accent: #FF6B00;
 25→  --color-accent-dark: #CC5500;
 26→  --color-accent-light: #FF8533;
 27→
 28→  /* Colores de Estado */
 29→  --color-success: #227A4B;
 30→  --color-warning: #FFAB00;
 31→  --color-error: #FF5630;
 32→  --color-info: #00B8D9;
 33→
 34→  /* Escala de Grises */
 35→  --color-gray-900: #091E42;
 36→  --color-gray-800: #172B4D;
 37→  --color-gray-700: #253858;
 38→  --color-gray-600: #344563;
 39→  --color-gray-500: #5E6C84;
 40→  --color-gray-400: #626F86;
 41→  --color-gray-300: #C1C7D0;
 42→  --color-gray-200: #DFE1E6;
 43→  --color-gray-100: #EBECF0;
 44→  --color-gray-50: #F4F5F7;
 45→
 46→  /* Colores Base */
 47→  --color-white: #FFFFFF;
 48→  --color-black: #000000;
 49→  --color-background: #F8FAFC;
 50→
 51→  /* Semantic Tokens (stable across themes — use for elements that need consistent contrast) */
 52→  --color-surface-card: var(--color-white);
 53→  --color-surface-elevated: var(--color-white);
 54→  --color-text-on-primary: #FFFFFF;
 55→  --color-text-on-accent: #FFFFFF;
 56→
 57→  /* Tipografía */
 58→  --font-family: 'Inter Variable', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
 59→  --font-size-xs: 0.75rem;
 60→  /* 12px */
 61→  --font-size-sm: 0.875rem;
 62→  /* 14px */
 63→  --font-size-base: 1rem;
 64→  /* 16px */
 65→  --font-size-lg: 1.125rem;
 66→  /* 18px */
 67→  --font-size-xl: 1.25rem;
 68→  /* 20px */
 69→  --font-size-2xl: 1.5rem;
 70→  /* 24px */
 71→  --font-size-3xl: 1.875rem;
 72→  /* 30px */
 73→  --font-size-4xl: 2.25rem;
 74→  /* 36px */
 75→  --font-size-5xl: 3rem;
 76→  /* 48px */
 77→  --font-size-6xl: 3.75rem;
 78→  /* 60px */
 79→
 80→  /* Pesos de Fuente */
 81→  --font-weight-regular: 400;
 82→  --font-weight-medium: 500;
 83→  --font-weight-semibold: 600;
 84→  --font-weight-bold: 700;
 85→  --font-weight-extrabold: 800;
 86→
 87→  /* Espaciado (Sistema de 8px) */
 88→  --spacing-1: 0.25rem;
 89→  /* 4px */
 90→  --spacing-2: 0.5rem;
 91→  /* 8px */
 92→  --spacing-3: 0.75rem;
 93→  /* 12px */
 94→  --spacing-4: 1rem;
 95→  /* 16px */
 96→  --spacing-5: 1.25rem;
 97→  /* 20px */
 98→  --spacing-6: 1.5rem;
 99→  /* 24px */
100→  --spacing-8: 2rem;
101→  /* 32px */
102→  --spacing-10: 2.5rem;
103→  /* 40px */
104→  --spacing-12: 3rem;
105→  /* 48px */
106→  --spacing-16: 4rem;
107→  /* 64px */
108→  --spacing-20: 5rem;
109→  /* 80px */
110→  --spacing-24: 6rem;
111→  /* 96px */
112→
113→  /* Border Radius */
114→  --radius-sm: 4px;
115→  --radius-md: 8px;
116→  --radius-lg: 12px;
117→  --radius-xl: 16px;
118→  --radius-2xl: 24px;
119→  --radius-full: 9999px;
120→
121→  /* Sombras */
122→  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
123→  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
124→  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
125→  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
126→  --shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
127→  --shadow-card: 0 2px 8px rgba(0, 82, 204, 0.08);
128→  --shadow-card-hover: 0 12px 24px rgba(0, 82, 204, 0.15);
129→
130→  /* Transiciones */
131→  --transition-fast: 150ms ease;
132→  --transition-base: 300ms ease;
133→  --transition-slow: 500ms ease;
134→  --transition-bounce: 500ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
135→
136→  /* Z-index */
137→  --z-dropdown: 100;
138→  --z-sticky: 200;
139→  --z-fixed: 300;
140→  --z-modal-backdrop: 400;
141→  --z-modal: 500;
142→  --z-popover: 600;
143→  --z-tooltip: 700;
144→
145→  /* Contenedor */
146→  --container-max: 1280px;
147→  --container-padding: var(--spacing-6);
148→}
149→
150→/* ============================================
151→   2. RESET Y BASE
152→   ============================================ */
153→*,
154→*::before,
155→*::after {
156→  box-sizing: border-box;
157→  margin: 0;
158→  padding: 0;
159→}
160→
161→html {
162→  -webkit-font-smoothing: antialiased;
163→  -moz-osx-font-smoothing: grayscale;
164→}
165→
166→body {
167→  font-family: var(--font-family);
168→  font-size: var(--font-size-base);
169→  font-weight: var(--font-weight-regular);
170→  line-height: 1.6;
171→  color: var(--color-gray-800);
172→  background-color: var(--color-background);
173→  overflow-x: hidden;
174→}
175→
176→img {
177→  max-width: 100%;
178→  height: auto;
179→  display: block;
180→}
181→
182→a {
183→  color: inherit;
184→  text-decoration: none;
185→}
186→
187→button {
188→  font-family: inherit;
189→  cursor: pointer;
190→  border: none;
191→  background: none;
192→}
193→
194→ul,
195→ol {
196→  list-style: none;
197→}
198→
199→input,
200→textarea,
201→select {
202→  font-family: inherit;
203→  font-size: inherit;
204→}
205→
206→/* ============================================
207→   3. UTILIDADES
208→   ============================================ */
209→.container {
210→  width: 100%;
211→  max-width: var(--container-max);
212→  margin: 0 auto;
213→  padding: 0 var(--container-padding);
214→}
215→
216→.section {
217→  padding: var(--spacing-16) 0;
218→  contain: layout style;
219→}
220→
221→.section-sm {
222→  padding: var(--spacing-12) 0;
223→}
224→
225→.section-lg {
226→  padding: var(--spacing-24) 0;
227→}
228→
229→/* Skip Link - Accesibilidad */
230→.skip-link {
231→  position: absolute;
232→  top: -100%;
233→  left: 50%;
234→  transform: translateX(-50%);
235→  background: var(--color-primary);
236→  color: var(--color-white);
237→  padding: var(--spacing-3) var(--spacing-6);
238→  border-radius: var(--radius-md);
239→  z-index: var(--z-tooltip);
240→  transition: top var(--transition-fast);
241→}
242→
243→.skip-link:focus {
244→  top: var(--spacing-4);
245→}
246→
247→/* Visualmente oculto */
248→.sr-only {
249→  position: absolute;
250→  width: 1px;
251→  height: 1px;
252→  padding: 0;
253→  margin: -1px;
254→  overflow: hidden;
255→  clip: rect(0, 0, 0, 0);
256→  white-space: nowrap;
257→  border: 0;
258→}
259→
260→/* ============================================
261→   4. TIPOGRAFÍA
262→   ============================================ */
263→h1,
264→h2,
265→h3,
266→h4,
267→h5,
268→h6 {
269→  font-weight: var(--font-weight-bold);
270→  line-height: 1.2;
271→  color: var(--color-gray-900);
272→}
273→
274→h1 {
275→  font-size: var(--font-size-5xl);
276→}
277→
278→h2 {
279→  font-size: var(--font-size-4xl);
280→}
281→
282→h3 {
283→  font-size: var(--font-size-3xl);
284→}
285→
286→h4 {
287→  font-size: var(--font-size-2xl);
288→}
289→
290→h5 {
291→  font-size: var(--font-size-xl);
292→}
293→
294→h6 {
295→  font-size: var(--font-size-lg);
296→}
297→
298→.section-title {
299→  font-size: var(--font-size-4xl);
300→  font-weight: var(--font-weight-bold);
301→  text-align: center;
302→  margin-bottom: var(--spacing-4);
303→  color: var(--color-gray-900);
304→}
305→
306→.section-subtitle {
307→  font-size: var(--font-size-lg);
308→  color: var(--color-gray-500);
309→  text-align: center;
310→  max-width: 600px;
311→  margin: 0 auto var(--spacing-12);
312→}
313→
314→.text-gradient {
315→  background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-accent) 100%);
316→  -webkit-background-clip: text;
317→  -webkit-text-fill-color: transparent;
318→  background-clip: text;
319→}
320→
321→/* ============================================
322→   5. BOTONES
323→   ============================================ */
324→.btn {
325→  display: inline-flex;
326→  align-items: center;
327→  justify-content: center;
328→  gap: var(--spacing-2);
329→  padding: var(--spacing-3) var(--spacing-6);
330→  font-size: var(--font-size-base);
331→  font-weight: var(--font-weight-semibold);
332→  line-height: 1.5;
333→  border-radius: var(--radius-md);
334→  transition: background var(--transition-base), color var(--transition-base), transform var(--transition-base), box-shadow var(--transition-base);
335→  position: relative;
336→  overflow: hidden;
337→}
338→
339→.btn::before {
340→  content: '';
341→  position: absolute;
342→  top: 0;
343→  left: -100%;
344→  width: 100%;
345→  height: 100%;
346→  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
347→  transition: left var(--transition-slow);
348→}
349→
350→.btn:hover::before {
351→  left: 100%;
352→}
353→
354→.btn-primary {
355→  background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-dark) 100%);
356→  color: var(--color-text-on-primary);
357→  box-shadow: 0 4px 14px rgba(0, 82, 204, 0.4);
358→}
359→
360→.btn-primary:hover {
361→  transform: translateY(-2px);
362→  box-shadow: 0 6px 20px rgba(0, 82, 204, 0.5);
363→}
364→
365→.btn-primary:active {
366→  transform: translateY(0);
367→}
368→
369→.btn-secondary {
370→  background: transparent;
371→  color: var(--color-primary);
372→  border: 2px solid var(--color-primary);
373→}
374→
375→.btn-secondary:hover {
376→  background: var(--color-primary);
377→  color: var(--color-white);
378→  transform: translateY(-2px);
379→}
380→
381→.btn-accent {
382→  background: linear-gradient(135deg, var(--color-accent) 0%, var(--color-accent-dark) 100%);
383→  color: var(--color-text-on-accent);
384→  box-shadow: 0 4px 14px rgba(255, 107, 0, 0.4);
385→}
386→
387→.btn-accent:hover {
388→  transform: translateY(-2px);
389→  box-shadow: 0 6px 20px rgba(255, 107, 0, 0.5);
390→}
391→
392→.btn-white {
393→  background: var(--color-white);
394→  color: var(--color-primary);
395→  box-shadow: var(--shadow-md);
396→}
397→
398→.btn-white:hover {
399→  transform: translateY(-2px);
400→  box-shadow: var(--shadow-lg);
401→}
402→
403→.btn-lg {
404→  padding: var(--spacing-4) var(--spacing-8);
405→  font-size: var(--font-size-lg);
406→}
407→
408→.btn-sm {
409→  padding: var(--spacing-2) var(--spacing-4);
410→  font-size: var(--font-size-sm);
411→}
412→
413→.btn-icon {
414→  width: 44px;
415→  height: 44px;
416→  padding: 0;
417→  border-radius: var(--radius-full);
418→}
419→
420→.btn:disabled {
421→  opacity: 0.6;
422→  cursor: not-allowed;
423→  transform: none !important;
424→}
425→
426→/* Focus visible para accesibilidad */
427→.btn:focus-visible {
428→  outline: 3px solid var(--color-primary-light);
429→  outline-offset: 2px;
430→}

  1→/* ============================================
  2→   Product Card — Shared Styles
  3→   Used by ProductCard.astro (static) and
  4→   ProductFilter.tsx (interactive island)
  5→   ============================================ */
  6→
  7→.product-card {
  8→  position: relative;
  9→  display: flex;
 10→  flex-direction: column;
 11→  background: var(--color-surface-card);
 12→  border-radius: var(--radius-xl);
 13→  overflow: hidden;
 14→  transition: transform 0.25s ease, box-shadow 0.25s ease, opacity 0.4s ease;
 15→  box-shadow: var(--shadow-card);
 16→  cursor: pointer;
 17→  contain: layout style paint;
 18→  text-decoration: none;
 19→  color: inherit;
 20→}
 21→
 22→.product-card:hover {
 23→  transform: translateY(-6px);
 24→  box-shadow: var(--shadow-card-hover);
 25→}
 26→
 27→.product-card:focus-visible {
 28→  outline: 3px solid var(--color-primary-light);
 29→  outline-offset: 2px;
 30→}
 31→
 32→/* Link wrapper used in static ProductCard.astro */
 33→.product-link {
 34→  display: block;
 35→  color: inherit;
 36→}
 37→
 38→.product-image {
 39→  position: relative;
 40→  height: 220px;
 41→  aspect-ratio: 3/2;
 42→  background: linear-gradient(135deg, rgba(0, 82, 204, 0.04) 0%, rgba(0, 82, 204, 0.01) 100%);
 43→  display: flex;
 44→  align-items: center;
 45→  justify-content: center;
 46→  overflow: hidden;
 47→  padding: var(--spacing-4);
 48→}
 49→
 50→.product-image img {
 51→  width: 100%;
 52→  height: 100%;
 53→  object-fit: contain;
 54→  transition: transform 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
 55→}
 56→
 57→.product-card:hover .product-image img {
 58→  transform: scale(1.08);
 59→}
 60→
 61→.product-image svg {
 62→  width: 120px;
 63→  height: 120px;
 64→  color: var(--color-gray-400);
 65→  transition: transform var(--transition-base), color var(--transition-base);
 66→}
 67→
 68→.product-card:hover .product-image svg {
 69→  transform: scale(1.1);
 70→  color: var(--color-primary);
 71→}
 72→
 73→.product-badge {
 74→  position: absolute;
 75→  top: var(--spacing-3);
 76→  left: var(--spacing-3);
 77→  padding: var(--spacing-1) var(--spacing-3);
 78→  background: var(--color-primary);
 79→  color: var(--color-text-on-primary);
 80→  font-size: var(--font-size-xs);
 81→  font-weight: var(--font-weight-semibold);
 82→  border-radius: var(--radius-full);
 83→  text-transform: uppercase;
 84→  letter-spacing: 0.5px;
 85→  z-index: 2;
 86→  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
 87→}
 88→
 89→.badge-popular {
 90→  background: linear-gradient(135deg, #FF6B00 0%, #FF8C38 100%);
 91→}
 92→
 93→.badge-pro {
 94→  background: linear-gradient(135deg, #0052CC 0%, #2684FF 100%);
 95→}
 96→
 97→.product-content {
 98→  padding: var(--spacing-5) var(--spacing-6);
 99→}
100→
101→/* Meta row: category + variant indicator */
102→.product-meta-row {
103→  display: flex;
104→  align-items: center;
105→  gap: var(--spacing-2);
106→  margin-bottom: var(--spacing-2);
107→  flex-wrap: wrap;
108→}
109→
110→.product-category {
111→  display: inline-block;
112→  padding: var(--spacing-1) var(--spacing-2);
113→  background: var(--color-primary-50);
114→  color: var(--color-primary);
115→  font-size: var(--font-size-xs);
116→  font-weight: var(--font-weight-medium);
117→  border-radius: var(--radius-sm);
118→}
119→
120→.variant-indicator {
121→  display: inline-flex;
122→  align-items: center;
123→  gap: 3px;
124→  padding: var(--spacing-1) var(--spacing-2);
125→  background: var(--color-accent, #FF6B00);
126→  color: #fff;
127→  font-size: 11px;
128→  font-weight: var(--font-weight-semibold);
129→  border-radius: var(--radius-sm);
130→}
131→
132→/* Application tags */
133→.application-tags {
134→  display: flex;
135→  gap: var(--spacing-1);
136→  margin-bottom: var(--spacing-2);
