  5→
  6→[data-theme="dark"] {
  7→  /* Colores de fondo */
  8→  --color-background: #0f172a;
  9→  --color-white: #1e293b;
 10→
 11→  /* Escala de grises invertida */
 12→  --color-gray-50: #1e293b;
 13→  --color-gray-100: #334155;
 14→  --color-gray-200: #475569;
 15→  --color-gray-300: #8893a7;
 16→  --color-gray-400: #94a3b8;
 17→  --color-gray-500: #cbd5e1;
 18→  --color-gray-600: #e2e8f0;
 19→  --color-gray-700: #f1f5f9;
 20→  --color-gray-800: #f8fafc;
 21→  --color-gray-900: #ffffff;
 22→
 23→  /* Colores primarios ajustados */
 24→  --color-primary-50: rgba(0, 82, 204, 0.2);
 25→  --color-primary-light: #60a5fa;
 26→
 27→  /* Semantic tokens for dark mode */
 28→  --color-surface-card: #1e293b;
 29→  --color-surface-elevated: #334155;
 30→  --color-text-on-primary: #FFFFFF;
 31→  --color-text-on-accent: #FFFFFF;
 32→
 33→  /* Sombras */
 34→  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.3);
 35→  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.4), 0 2px 4px -1px rgba(0, 0, 0, 0.3);
 36→  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.4), 0 4px 6px -2px rgba(0, 0, 0, 0.3);
 37→  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.4), 0 10px 10px -5px rgba(0, 0, 0, 0.3);
 38→  --shadow-card: 0 2px 8px rgba(0, 0, 0, 0.3);
 39→  --shadow-card-hover: 0 12px 24px rgba(0, 0, 0, 0.4);
 40→}
