305→  background: #1e293b;
306→}
307→
308→:global([data-theme="dark"]) .quote-empty-icon {
309→  background: #334155;
310→  color: #b0bec5;
311→}
312→
313→:global([data-theme="dark"]) .quote-empty h3 {
