350→  .remove-item-btn {
351→    position: absolute;
352→    top: var(--spacing-2);
353→    right: var(--spacing-2);
354→  }
355→}

  1→/* ============================================
  2→   VariantSelector — CSS Module
  3→   Multi-step configurator + flat mode.
  4→   ============================================ */
  5→
  6→/* ---- Base container ---- */
  7→
  8→.selector {
  9→  border-top: 3px solid var(--color-primary);
 10→  padding-top: var(--spacing-4);
 11→}
 12→
 13→/* ---- Model / Subtype info (flat mode) ---- */
 14→
