 22→  font-size: var(--font-size-xs);
 23→  font-weight: var(--font-weight-bold);
 24→  border-radius: var(--radius-full);
 25→  text-transform: uppercase;
 26→  letter-spacing: 0.05em;
 27→  z-index: 3;
 28→  pointer-events: none;
 29→}
 30→
 31→/* Main image area */
 32→.gallery-main {
 33→  position: relative;
 34→  overflow: hidden;
 35→  border-radius: var(--radius-lg);
 36→  cursor: zoom-in;
 37→  display: flex;
 38→  align-items: center;
 39→  justify-content: center;
 40→  min-height: 280px;
 41→  padding: var(--spacing-6);
 42→  background: linear-gradient(135deg, var(--color-gray-100) 0%, var(--color-gray-50) 100%);
 43→}
 44→
 45→.gallery-main-img {
 46→  max-width: 100%;
 47→  max-height: 400px;
 48→  object-fit: contain;
 49→  transition: transform 0.15s ease-out;
 50→  transform-origin: center center;
