 57→}
 58→
 59→/* Thumbnails */
 60→.gallery-thumbs {
 61→  display: flex;
 62→  gap: var(--spacing-2);
 63→  overflow-x: auto;
 64→  padding: var(--spacing-1) 0;
 65→  scrollbar-width: auto;
 66→  scrollbar-color: var(--color-gray-300) var(--color-gray-100);
 67→}
 68→
 69→.gallery-thumbs::-webkit-scrollbar {
 70→  height: 8px;
 71→}
 72→
 73→.gallery-thumbs::-webkit-scrollbar-track {
 74→  background: var(--color-gray-100);
 75→  border-radius: 4px;
 76→}
 77→
 78→.gallery-thumbs::-webkit-scrollbar-thumb {
 79→  background: var(--color-gray-300);
 80→  border-radius: 4px;
 81→}
 82→
 83→.gallery-thumbs::-webkit-scrollbar-thumb:hover {
 84→  background: var(--color-gray-400);
 85→}
 86→
 87→.gallery-thumb {
 88→  flex-shrink: 0;
 89→  width: 64px;
 90→  height: 64px;
 91→  border: 2px solid var(--color-gray-200);
 92→  border-radius: var(--radius-md);
 93→  overflow: hidden;
 94→  cursor: pointer;
 95→  background: var(--color-gray-50);
 96→  padding: var(--spacing-1);
 97→  transition: border-color 0.2s ease, box-shadow 0.2s ease;
 98→}
 99→
100→.gallery-thumb:hover {
101→  border-color: var(--color-primary);
102→}
103→
104→.gallery-thumb.active {
105→  border-color: var(--color-primary);
106→  box-shadow: 0 0 0 2px rgba(0, 82, 204, 0.2);
107→}
108→
109→.gallery-thumb img {
110→  width: 100%;
111→  height: 100%;
112→  object-fit: contain;
113→}
114→
115→/* ============================================
116→   Lightbox (rendered via createPortal to body)
117→   ============================================ */
118→.lightbox {
119→  position: fixed;
120→  inset: 0;
121→  z-index: 9999;
122→  display: flex;
123→  align-items: center;
124→  justify-content: center;
125→  animation: lightboxFadeIn 0.2s ease;
126→}
127→
128→.lightbox-overlay {
129→  position: absolute;
130→  inset: 0;
131→  background: rgba(0, 0, 0, 0.92);
132→}
133→
134→/* Close button */
135→.lightbox-close {
136→  position: absolute;
137→  top: var(--spacing-4);
138→  right: var(--spacing-4);
139→  width: 44px;
140→  height: 44px;
141→  background: rgba(255, 255, 255, 0.1);
142→  border: none;
143→  border-radius: var(--radius-full);
144→  cursor: pointer;
145→  display: flex;
146→  align-items: center;
147→  justify-content: center;
148→  color: var(--color-white);
149→  transition: background 0.2s ease;
150→  z-index: 2;
151→}
152→
153→.lightbox-close:hover {
154→  background: rgba(255, 255, 255, 0.25);
155→}
156→
157→.lightbox-close:focus-visible {
158→  outline: 2px solid var(--color-white);
159→  outline-offset: 2px;
160→}
161→
162→/* Counter */
163→.lightbox-counter {
164→  position: absolute;
165→  top: var(--spacing-4);
166→  left: var(--spacing-4);
167→  color: rgba(255, 255, 255, 0.7);
168→  font-size: var(--font-size-sm);
169→  font-weight: var(--font-weight-medium);
170→  z-index: 2;
171→  user-select: none;
172→}
173→
174→/* Navigation arrows */
175→.lightbox-arrow {
176→  position: absolute;
177→  top: 50%;
178→  transform: translateY(-50%);
179→  width: 48px;
180→  height: 48px;
181→  background: rgba(255, 255, 255, 0.1);
182→  border: none;
183→  border-radius: var(--radius-full);
184→  cursor: pointer;
185→  display: flex;
186→  align-items: center;
187→  justify-content: center;
188→  color: var(--color-white);
189→  transition: background 0.2s ease;
190→  z-index: 2;
191→}
192→
193→.lightbox-arrow:hover {
194→  background: rgba(255, 255, 255, 0.25);
195→}
196→
197→.lightbox-arrow:focus-visible {
198→  outline: 2px solid var(--color-white);
199→  outline-offset: 2px;
200→}
201→
202→.lightbox-prev {
203→  left: var(--spacing-4);
204→}
205→
206→.lightbox-next {
207→  right: var(--spacing-4);
208→}
209→
210→/* Image container */
211→.lightbox-image-wrap {
212→  position: relative;
213→  z-index: 1;
214→  max-width: 90vw;
215→  max-height: 85vh;
216→  display: flex;
217→  align-items: center;
218→  justify-content: center;
219→}
220→
221→.lightbox-image {
222→  max-width: 90vw;
223→  max-height: 85vh;
224→  object-fit: contain;
225→  animation: lightboxImgIn 0.25s ease;
226→  user-select: none;
227→  -webkit-user-drag: none;
228→}
229→
230→/* Animations */
231→@keyframes lightboxFadeIn {
232→  from { opacity: 0; }
233→  to { opacity: 1; }
234→}
235→
236→@keyframes lightboxImgIn {
237→  from { opacity: 0; transform: scale(0.95); }
238→  to { opacity: 1; transform: scale(1); }
239→}
240→
241→/* ============================================
242→   Dark mode
243→   ============================================ */
244→:global([data-theme="dark"]) .gallery-main {
245→  background: linear-gradient(135deg, var(--color-gray-100) 0%, var(--color-gray-50) 100%);
246→}
247→
248→:global([data-theme="dark"]) .gallery-thumb {
249→  border-color: var(--color-gray-200);
250→  background: var(--color-gray-100);
251→}
252→
253→:global([data-theme="dark"]) .gallery-thumb.active {
