255→  box-shadow: 0 0 0 2px rgba(0, 82, 204, 0.3);
256→}
257→
258→:global([data-theme="dark"]) .lightbox-overlay {
259→  background: rgba(0, 0, 0, 0.95);
260→}
261→
262→:global([data-theme="dark"]) .gallery-badge {
263→  color: #ffffff;
264→}
265→
266→:global([data-theme="dark"]) .lightbox-close,
267→:global([data-theme="dark"]) .lightbox-arrow {
268→  color: #ffffff;
269→}
270→
271→/* ============================================
272→   Responsive
273→   ============================================ */
274→@media (max-width: 768px) {
275→  .gallery-main {
276→    min-height: 220px;
