297→    height: 40px;
298→  }
299→
300→  .lightbox-prev {
301→    left: var(--spacing-2);
302→  }
303→
304→  .lightbox-next {
305→    right: var(--spacing-2);
306→  }
307→
308→  .lightbox-image {
309→    max-width: 95vw;
310→    max-height: 80vh;
311→  }
312→}
313→
314→@media (max-width: 480px) {
