  1→---
  2→import MainLayout from '../../layouts/MainLayout.astro';
  3→import Breadcrumb from '../../components/Breadcrumb.astro';
  4→import ProductFilter from '../../components/islands/ProductFilter.tsx';
  5→import { getCollection } from 'astro:content';
  6→import categoriesData from '../../data/categories.json';
  7→import type { ProductSlim, Category } from '../../types';
  8→
  9→const allProducts = await getCollection('products');
 10→
 11→// Map to slim data for island serialization
 12→const products: ProductSlim[] = allProducts.map(p => ({
 13→  sku: p.data.sku,
 14→  title: p.data.title,
 15→  category: p.data.category,
 16→  categorySlug: p.data.categorySlug,
 17→  description: p.data.description,
 18→  image: p.data.image,
 19→  inStock: p.data.inStock,
 20→  badge: p.data.badge,
 21→  subcategories: p.data.subcategories,
 22→  hasVariants: (p.data.variants?.length ?? 0) > 0,
 23→}));
 24→
 25→const categories = categoriesData as Category[];
 26→---
 27→
 28→<MainLayout title="Productos — GelChile" description="Catálogo completo de productos para sistemas de puesta a tierra y protección eléctrica">
 29→  <!-- Page Hero -->
 30→  <section class="page-hero">
 31→    <div class="container">
 32→      <h1 class="page-hero-title">Nuestros Productos</h1>
 33→      <p class="page-hero-subtitle">
 34→        Soluciones profesionales en puesta a tierra y protección eléctrica
 35→      </p>
 36→    </div>
 37→  </section>
 38→
 39→  <Breadcrumb items={[{ label: 'Inicio', href: '/' }, { label: 'Productos' }]} />
 40→
 41→  <!-- Product Filter + Grid -->
 42→  <section class="section products-section">
 43→    <div class="container">
 44→      <h2 class="sr-only">Catálogo de productos</h2>
 45→      <ProductFilter client:load products={products} categories={categories} />
 46→    </div>
 47→  </section>
 48→
 49→  <!-- CTA -->
 50→  <section class="cta-section">
 51→    <div class="container">
 52→      <div class="cta-content">
 53→        <h2 class="cta-title">¿No encuentras lo que buscas?</h2>
 54→        <p class="cta-description">
 55→          Contáctanos directamente y te ayudaremos a encontrar la solución perfecta
 56→          para tu sistema de puesta a tierra.
 57→        </p>
 58→        <div class="cta-buttons">
 59→          <a href="/cotizacion" class="btn btn-white btn-lg">Solicitar Cotización</a>
 60→          <a href="mailto:ventas@gelchile.cl" class="btn btn-accent btn-lg">Escríbenos</a>
 61→        </div>
 62→      </div>
 63→    </div>
 64→  </section>
 65→</MainLayout>
 66→
 67→<script>
 68→  // Save/restore scroll position so "Volver al Catálogo" returns to the same spot
 69→  const SCROLL_KEY = 'gelchile_catalog_scroll';
 70→
 71→  // Restore on load
 72→  const saved = sessionStorage.getItem(SCROLL_KEY);
 73→  if (saved) {
 74→    const y = parseInt(saved, 10);
 75→    // Wait for content to render before scrolling
 76→    requestAnimationFrame(() => {
 77→      window.scrollTo(0, y);
 78→    });
 79→  }
 80→
 81→  // Save on scroll (debounced — 500ms to reduce main-thread work)
 82→  let scrollTimer: ReturnType<typeof setTimeout>;
 83→  window.addEventListener('scroll', () => {
 84→    clearTimeout(scrollTimer);
 85→    scrollTimer = setTimeout(() => {
 86→      sessionStorage.setItem(SCROLL_KEY, String(window.scrollY));
 87→    }, 500);
 88→  }, { passive: true });
 89→</script>
