180→
181→  /* Responsive */
182→  @media (max-width: 768px) {
183→    .page-hero {
184→      padding: calc(64px + var(--spacing-8)) 0 var(--spacing-8);
185→    }
186→
187→    .page-hero-title {
188→      font-size: var(--font-size-2xl);
189→    }
190→
191→    .cta-title {
192→      font-size: var(--font-size-2xl);
193→    }
194→
195→    .cta-section {
196→      padding: var(--spacing-12) 0;
197→    }
198→  }
199→
200→  @media (max-width: 480px) {
201→    .cta-buttons {
202→      flex-direction: column;
203→      align-items: center;
204→    }
205→  }
206→</style>
