  1→import { useState, useEffect, useRef, useMemo, useCallback } from 'preact/hooks';
  2→import { useStore } from '@nanostores/preact';
  3→import { $cartSkuMap, addItem } from '../../stores/cart';
  4→import { showToast } from '../../stores/toast';
  5→import type { ProductSlim, Category } from '../../types';
  6→import { slugify } from '../../utils/slugify';
  7→import { applicationTags } from '../../data/application-tags';
  8→import { getCategoryPlaceholderSvg } from '../../utils/category-placeholders';
  9→import styles from './ProductFilter.module.css';
 10→
 11→interface Props {
 12→  products: ProductSlim[];
 13→  categories: Category[];
 14→}
 15→
 16→// H7: Sort options
 17→type SortOption = 'default' | 'name-asc' | 'name-desc' | 'category';
 18→
 19→const sortLabels: Record<SortOption, string> = {
 20→  'default': 'Destacados',
 21→  'name-asc': 'Nombre A-Z',
 22→  'name-desc': 'Nombre Z-A',
 23→  'category': 'Categoría',
 24→};
 25→
 26→/** Product filter + search + grid. Mount with client:load on /productos. */
 27→export default function ProductFilter({ products, categories }: Props) {
 28→  const [selectedCategory, setSelectedCategory] = useState<string>('todos');
 29→  const [selectedSubcategory, setSelectedSubcategory] = useState<string>('todos');
 30→  const [searchText, setSearchText] = useState('');
 31→  const [inputValue, setInputValue] = useState('');
 32→  const [sortBy, setSortBy] = useState<SortOption>('default');
 33→  const cartSkuMap = useStore($cartSkuMap);
 34→  const searchInputRef = useRef<HTMLInputElement>(null);
 35→  const debounceRef = useRef<ReturnType<typeof setTimeout>>();
 36→
 37→  const handleSearchInput = (value: string) => {
 38→    setInputValue(value);
 39→    clearTimeout(debounceRef.current);
 40→    debounceRef.current = setTimeout(() => setSearchText(value), 200);
 41→  };
 42→
 43→  // Read initial category from URL on mount
 44→  useEffect(() => {
 45→    if (typeof window === 'undefined') return;
 46→    const params = new URLSearchParams(window.location.search);
 47→    const cat = params.get('categoria');
 48→    if (cat && categories.some((c) => c.id === cat)) {
 49→      setSelectedCategory(cat);
 50→    }
 51→  }, []);
 52→
 53→  // Update URL when filter changes
 54→  useEffect(() => {
 55→    if (typeof window === 'undefined') return;
 56→    const url = new URL(window.location.href);
 57→    if (selectedCategory === 'todos') {
 58→      url.searchParams.delete('categoria');
 59→    } else {
 60→      url.searchParams.set('categoria', selectedCategory);
 61→    }
 62→    window.history.replaceState({}, '', url.toString());
 63→  }, [selectedCategory]);
 64→
 65→  // H7: Cmd+K / Ctrl+K keyboard shortcut to focus search
 66→  useEffect(() => {
 67→    if (typeof window === 'undefined') return;
 68→    const handleKeyDown = (e: KeyboardEvent) => {
 69→      // Cmd+K on Mac, Ctrl+K on Windows/Linux
 70→      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
 71→        e.preventDefault();
 72→        searchInputRef.current?.focus();
 73→        searchInputRef.current?.select();
 74→      }
 75→      // Escape to blur search and clear
 76→      if (e.key === 'Escape' && document.activeElement === searchInputRef.current) {
 77→        searchInputRef.current?.blur();
 78→        if (inputValue) {
 79→          clearTimeout(debounceRef.current);
 80→          setInputValue('');
 81→          setSearchText('');
 82→        }
 83→      }
 84→    };
 85→    document.addEventListener('keydown', handleKeyDown);
 86→    return () => document.removeEventListener('keydown', handleKeyDown);
 87→  }, [inputValue]);
 88→
 89→  // Filter products (memoized)
 90→  const query = searchText.toLowerCase().trim();
 91→  const filtered = useMemo(() => products.filter((p) => {
 92→    const matchCategory = selectedCategory === 'todos' || p.categorySlug === selectedCategory;
 93→    const matchSubcategory = selectedSubcategory === 'todos' || (p.subcategories?.includes(selectedSubcategory) ?? false);
 94→    const matchSearch = !query ||
 95→      p.title.toLowerCase().includes(query) ||
 96→      p.sku.toLowerCase().includes(query) ||
 97→      p.description.toLowerCase().includes(query);
 98→    return matchCategory && matchSubcategory && matchSearch;
 99→  }), [products, selectedCategory, selectedSubcategory, query]);
100→
101→  // Extract subcategories from products in the selected category (memoized)
102→  const subcategories = useMemo(() => selectedCategory !== 'todos'
103→    ? [...new Set(
104→      products
105→        .filter((p) => p.categorySlug === selectedCategory && p.subcategories?.length)
106→        .flatMap((p) => p.subcategories!)
107→    )].sort((a, b) => a.localeCompare(b, 'es'))
108→    : [], [products, selectedCategory]);
109→
110→  // H7: Sort filtered products (memoized)
111→  const sorted = useMemo(() => [...filtered].sort((a, b) => {
112→    switch (sortBy) {
113→      case 'name-asc':
114→        return a.title.localeCompare(b.title, 'es');
115→      case 'name-desc':
116→        return b.title.localeCompare(a.title, 'es');
117→      case 'category':
118→        return a.category.localeCompare(b.category, 'es');
119→      default:
120→        // Default: prioritize products with badges, then by order in array
121→        if (a.badge && !b.badge) return -1;
122→        if (!a.badge && b.badge) return 1;
123→        return 0;
124→    }
125→  }), [filtered, sortBy]);
126→
127→  const handleCategoryClick = useCallback((catId: string) => {
128→    setSelectedCategory(catId);
129→    setSelectedSubcategory('todos');
130→  }, []);
131→
132→  const getProductUrl = useCallback((product: ProductSlim) => `/productos/${slugify(product.title)}`, []);
133→
134→  const handleAddToQuote = useCallback((e: Event, product: ProductSlim) => {
135→    e.stopPropagation();
136→    e.preventDefault();
137→    if (product.hasVariants) {
138→      window.location.href = getProductUrl(product);
139→      return;
140→    }
141→    addItem({ sku: product.sku, title: product.title, category: product.category, quantity: 1, image: product.image });
142→    showToast(`${product.title} agregado a la cotización`, 'success');
143→  }, [getProductUrl]);
144→
145→  const getCartCount = useCallback((sku: string) => cartSkuMap.get(sku) || 0, [cartSkuMap]);
146→
147→  // Scroll reveal for dynamically rendered product cards
148→  const gridRef = useRef<HTMLDivElement>(null);
149→  const observerRef = useRef<IntersectionObserver | null>(null);
150→
151→  // Create observer once
152→  useEffect(() => {
153→    if (typeof window === 'undefined' || !('IntersectionObserver' in window)) return;
154→    observerRef.current = new IntersectionObserver(
155→      (entries) => {
156→        entries.forEach((entry) => {
157→          if (entry.isIntersecting) {
158→            entry.target.classList.add('aos-animate');
159→            observerRef.current?.unobserve(entry.target);
160→          }
161→        });
162→      },
163→      { threshold: 0.05, rootMargin: '50px' }
164→    );
165→    return () => observerRef.current?.disconnect();
166→  }, []);
167→
168→  // Observe only new cards when filter results change
169→  useEffect(() => {
170→    if (!gridRef.current || !observerRef.current) return;
171→    const observer = observerRef.current;
172→    const cards = gridRef.current.querySelectorAll('[data-aos]');
173→    if (!cards.length) return;
174→    if (!('IntersectionObserver' in window)) {
175→      cards.forEach((el) => el.classList.add('aos-animate'));
176→      return;
177→    }
178→    requestAnimationFrame(() => {
179→      cards.forEach((el) => {
180→        if (!el.classList.contains('aos-animate')) {
181→          observer.observe(el);
182→        }
183→      });
184→    });
185→  }, [sorted.length, selectedCategory, selectedSubcategory, searchText]);
186→
187→  return (
188→    <div>
189→      {/* Search with H7: keyboard shortcut indicator */}
190→      <div class={styles['search-container']}>
191→        <div class={styles['search-wrapper']}>
192→          <svg class={styles['search-icon']} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="20" height="20" aria-hidden="true">
193→            <circle cx="11" cy="11" r="8" />
194→            <line x1="21" y1="21" x2="16.65" y2="16.65" />
195→          </svg>
196→          <input
197→            ref={searchInputRef}
198→            type="text"
199→            id="product-search"
200→            aria-label="Buscar productos"
201→            class={styles['search-input']}
202→            placeholder="Buscar productos..."
203→            value={inputValue}
204→            onInput={(e) => handleSearchInput((e.target as HTMLInputElement).value)}
205→          />
206→          {inputValue ? (
207→            <button
208→              class={styles['search-clear']}
209→              onClick={() => {
210→                clearTimeout(debounceRef.current);
211→                setInputValue('');
212→                setSearchText('');
213→                searchInputRef.current?.focus();
214→              }}
215→              aria-label="Limpiar búsqueda"
216→              type="button"
217→            >
218→              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="16" height="16" aria-hidden="true">
219→                <line x1="18" y1="6" x2="6" y2="18" />
220→                <line x1="6" y1="6" x2="18" y2="18" />
221→              </svg>
222→            </button>
223→          ) : (
224→            <kbd class={styles['search-shortcut']}>⌘K</kbd>
225→          )}
226→        </div>
227→      </div>
228→
229→      {/* Filter buttons */}
230→      <div class={styles['filter-container']}>
231→        <button
232→          class={`${styles['filter-btn']} ${selectedCategory === 'todos' ? styles.active : ''}`}
233→          onClick={() => handleCategoryClick('todos')}
234→          aria-pressed={selectedCategory === 'todos'}
235→        >
236→          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="16" height="16" aria-hidden="true"><rect x="3" y="3" width="7" height="7" rx="1" /><rect x="14" y="3" width="7" height="7" rx="1" /><rect x="3" y="14" width="7" height="7" rx="1" /><rect x="14" y="14" width="7" height="7" rx="1" /></svg>
237→          Todos
238→        </button>
239→        {categories.map((cat) => (
240→          <button
241→            key={cat.id}
242→            class={`${styles['filter-btn']} ${selectedCategory === cat.id ? styles.active : ''}`}
243→            onClick={() => handleCategoryClick(cat.id)}
244→            aria-pressed={selectedCategory === cat.id}
245→          >
246→            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="16" height="16" aria-hidden="true">
247→              {cat.id === 'electrodos-electromagneticos' && <><circle cx="12" cy="12" r="9" /><path d="M12 3v18M3 12h18" /></>}
248→              {cat.id === 'electrodos-grafito' && <><path d="M12 2v20" /><path d="M8 6l4-4 4 4" /><line x1="8" y1="22" x2="16" y2="22" /></>}
249→              {cat.id === 'barras-quimicas' && <><rect x="8" y="2" width="8" height="20" rx="2" /><line x1="8" y1="8" x2="16" y2="8" /><line x1="8" y1="14" x2="16" y2="14" /></>}
250→              {cat.id === 'aditivos' && <><path d="M10 2v6l-2 4v6a2 2 0 002 2h4a2 2 0 002-2v-6l-2-4V2" /><line x1="8" y1="2" x2="16" y2="2" /></>}
251→              {cat.id === 'pararrayos' && <><path d="M13 2L3 14h9l-1 8 10-12h-9l1-8z" /></>}
252→              {cat.id === 'soldadura-exotermica' && <><circle cx="12" cy="12" r="3" /><path d="M12 1v4M12 19v4M4.22 4.22l2.83 2.83M16.95 16.95l2.83 2.83M1 12h4M19 12h4M4.22 19.78l2.83-2.83M16.95 7.05l2.83-2.83" /></>}
253→              {cat.id === 'moldes-grafito' && <><rect x="3" y="3" width="18" height="18" rx="2" /><path d="M9 3v18" /><path d="M15 3v18" /><path d="M3 9h18" /><path d="M3 15h18" /></>}
254→              {cat.id === 'accesorios' && <><path d="M14.7 6.3a1 1 0 000 1.4l1.6 1.6a1 1 0 001.4 0l3.7-3.8a6 6 0 01-7.4 7.4l-10 10a2 2 0 01-2.8-2.8l10-10a6 6 0 017.4-7.4l-3.8 3.7z" /></>}
255→              {cat.id === 'servicios' && <><path d="M20 21v-2a4 4 0 00-4-4H8a4 4 0 00-4 4v2" /><circle cx="12" cy="7" r="4" /></>}
256→            </svg>
257→            {cat.name}
258→          </button>
259→        ))}
260→      </div>
261→
262→      {/* Subcategory filter pills */}
263→      {subcategories.length > 1 && (
264→        <div class={styles['subcategory-container']}>
265→          <button
266→            class={`${styles['subcategory-btn']} ${selectedSubcategory === 'todos' ? styles.active : ''}`}
267→            onClick={() => setSelectedSubcategory('todos')}
268→            aria-pressed={selectedSubcategory === 'todos'}
269→          >
270→            Todos
271→          </button>
272→          {subcategories.map((sub) => (
273→            <button
274→              key={sub}
275→              class={`${styles['subcategory-btn']} ${selectedSubcategory === sub ? styles.active : ''}`}
276→              onClick={() => setSelectedSubcategory(sub)}
277→              aria-pressed={selectedSubcategory === sub}
278→            >
279→              {sub}
280→            </button>
281→          ))}
282→        </div>
283→      )}
284→
285→      {/* H7: Product count + sorting */}
286→      <div class={styles['results-bar']}>
287→        <p class={styles['product-count']}>
288→          {sorted.length} {sorted.length === 1 ? 'producto encontrado' : 'productos encontrados'}
289→        </p>
290→        <div class={styles['sort-container']}>
291→          <label class={styles['sort-label']} htmlFor="sort-select">Ordenar:</label>
292→          <select
293→            id="sort-select"
294→            class={styles['sort-select']}
295→            value={sortBy}
296→            onChange={(e) => setSortBy((e.target as HTMLSelectElement).value as SortOption)}
297→          >
298→            {Object.entries(sortLabels).map(([value, label]) => (
299→              <option key={value} value={value}>{label}</option>
300→            ))}
301→          </select>
302→        </div>
303→      </div>
304→
305→      {/* Product grid */}
306→      {sorted.length > 0 ? (
307→        <div class={styles['product-grid']} ref={gridRef}>
308→          {sorted.map((product, index) => {
309→            const count = getCartCount(product.sku);
310→            return (
311→              <a
312→                key={product.sku}
313→                href={getProductUrl(product)}
314→                class="product-card"
315→                data-aos="fade-up"
316→                data-aos-delay={index % 3 > 0 ? String((index % 3) * 75) : undefined}
317→                data-astro-prefetch="hover"
318→              >
319→                <div class="product-image">
320→                  {product.badge && (
321→                    <span class={`product-badge ${product.badge === 'Popular' ? 'badge-popular' : product.badge === 'Pro' ? 'badge-pro' : ''}`}>{product.badge}</span>
322→                  )}
323→                  {product.image ? (
324→                    <img src={product.image} alt={product.title} loading={index < 4 ? "eager" : "lazy"} fetchpriority={index < 4 ? "high" : undefined} decoding="async" width="300" height="200" sizes="(max-width: 480px) calc(100vw - 32px), (max-width: 768px) calc(50vw - 24px), (max-width: 1024px) calc(33.33vw - 24px), 280px" />
325→                  ) : (
326→                    <span dangerouslySetInnerHTML={{ __html: getCategoryPlaceholderSvg(product.categorySlug) }} />
327→                  )}
328→                </div>
329→                <div class="product-content">
330→                  <div class="product-meta-row">
331→                    <span class="product-category">{product.category}</span>
332→                    {product.hasVariants && (
333→                      <span class="variant-indicator">
334→                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="11" height="11" aria-hidden="true">
335→                          <rect x="3" y="3" width="7" height="7" rx="1" />
336→                          <rect x="14" y="3" width="7" height="7" rx="1" />
337→                          <rect x="3" y="14" width="7" height="7" rx="1" />
338→                          <rect x="14" y="14" width="7" height="7" rx="1" />
339→                        </svg>
340→                        Variantes
341→                      </span>
342→                    )}
343→                  </div>
344→                  {(applicationTags[product.categorySlug] || []).length > 0 && (
345→                    <div class="application-tags">
346→                      {(applicationTags[product.categorySlug] || []).map((tag) => (
347→                        <span key={tag} class="app-tag">{tag}</span>
348→                      ))}
349→                    </div>
350→                  )}
351→                  <h3 class="product-title">{product.title}</h3>
352→                  <p class="product-description">{product.description}</p>
353→                  <div class="product-sku-row">
354→                    <span class="product-sku">SKU: {product.sku}</span>
355→                    <span class={`card-stock ${!product.inStock ? 'out-stock' : ''}`}>
356→                      <span class="stock-dot"></span>
357→                      {product.inStock ? 'En Stock' : 'Sin Stock'}
358→                    </span>
359→                  </div>
360→                </div>
361→                <div class="product-footer">
362→                  <button
363→                    class={`btn-add-quote ${count > 0 ? 'added' : ''}`}
364→                    onClick={(e) => handleAddToQuote(e, product)}
365→                    aria-label={count > 0 ? `Agregado ${product.title} (${count})` : `Cotizar ${product.title}`}
366→                  >
367→                    {count > 0 ? (
368→                      <>
369→                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="18" height="18" aria-hidden="true">
370→                          <polyline points="20 6 9 17 4 12" />
371→                        </svg>
372→                        Agregado ({count})
373→                      </>
374→                    ) : (
375→                      <>
376→                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" width="18" height="18" aria-hidden="true">
377→                          <line x1="12" y1="5" x2="12" y2="19" />
378→                          <line x1="5" y1="12" x2="19" y2="12" />
379→                        </svg>
380→                        Cotizar
381→                      </>
382→                    )}
383→                  </button>
384→                </div>
385→              </a>
386→            );
387→          })}
388→        </div>
389→      ) : (
390→        <div class={styles['no-results']}>
391→          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" width="64" height="64" aria-hidden="true">
392→            <circle cx="11" cy="11" r="8" />
393→            <line x1="21" y1="21" x2="16.65" y2="16.65" />
394→            <line x1="8" y1="11" x2="14" y2="11" />
395→          </svg>
396→          <h3>No se encontraron productos</h3>
397→          <p>Intenta con otra búsqueda o categoría</p>
398→        </div>
399→      )}
400→    </div>
401→  );
402→}
