 1→// @ts-check
 2→import { defineConfig } from 'astro/config';
 3→import preact from '@astrojs/preact';
 4→import sitemap from '@astrojs/sitemap';
 5→
 6→// https://astro.build/config
 7→export default defineConfig({
 8→  site: 'https://gelchile.cl',
 9→  compressHTML: true,
10→  prefetch: {
11→    defaultStrategy: 'hover',
12→  },
13→  integrations: [
14→    preact(),
15→    sitemap({
16→      changefreq: 'weekly',
17→      priority: 0.7,
18→      lastmod: new Date(),
19→    }),
20→  ],
21→  vite: {
22→    build: {
23→      rollupOptions: {
24→        output: {
25→          manualChunks: {
26→            'preact-vendor': ['preact', 'preact/hooks', 'nanostores', '@nanostores/preact'],
27→          },
28→        },
29→      },
30→    },
31→  },
32→});
33→

  1→---
  2→import Header from '../components/Header.astro';
  3→import Footer from '../components/Footer.astro';
  4→import Toast from '../components/islands/Toast.tsx';
  5→import DarkModeToggle from '../components/islands/DarkModeToggle.tsx';
  6→import InstallPrompt from '../components/InstallPrompt.astro';
  7→import { siteConfig } from '../data/site-config';
  8→import '@fontsource-variable/inter';
  9→import interLatinUrl from '@fontsource-variable/inter/files/inter-latin-wght-normal.woff2?url';
 10→import '../styles/global.css';
 11→import '../styles/dark-mode.css';
 12→import '../styles/animations.css';
 13→
 14→interface Props {
 15→  title: string;
 16→  description?: string;
 17→  includeTurnstile?: boolean;
 18→  preloadImage?: string;
 19→}
 20→
 21→const { title, description = 'Sistemas de Puesta a Tierra y Protección Eléctrica', includeTurnstile = false, preloadImage } = Astro.props;
 22→const canonicalURL = new URL(Astro.url.pathname, Astro.site);
 23→---
 24→
 25→<!DOCTYPE html>
 26→<html lang="es">
 27→<head>
 28→  <meta charset="UTF-8" />
 29→  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
 30→  <meta name="description" content={description} />
 31→  <meta name="author" content="GelChile" />
 32→  <link rel="canonical" href={canonicalURL} />
 33→
 34→  <!-- Open Graph -->
 35→  <meta property="og:title" content={title} />
 36→  <meta property="og:description" content={description} />
 37→  <meta property="og:type" content="website" />
 38→  <meta property="og:locale" content="es_CL" />
 39→  <meta property="og:url" content={canonicalURL} />
 40→  <meta property="og:site_name" content="GelChile" />
 41→  <meta property="og:image" content={`${Astro.site}assets/img/gelchile-logo.webp`} />
 42→  <meta property="og:image:alt" content="GelChile - Sistemas de Puesta a Tierra" />
 43→
 44→  <!-- Twitter Card -->
 45→  <meta name="twitter:card" content="summary" />
 46→  <meta name="twitter:title" content={title} />
 47→  <meta name="twitter:description" content={description} />
 48→  <meta name="twitter:image" content={`${Astro.site}assets/img/gelchile-logo.webp`} />
 49→
 50→  <title>{title}</title>
 51→
 52→  <!-- Favicon -->
 53→  <link rel="icon" type="image/svg+xml" href="/favicon.svg" />
 54→
 55→  <!-- Preload Inter Latin font for faster LCP text rendering -->
 56→  <link rel="preload" as="font" type="font/woff2" href={interLatinUrl} crossorigin />
 57→
 58→  <!-- Preload hero/LCP image when specified -->
 59→  {preloadImage && (
 60→    <link rel="preload" as="image" type="image/webp" href={preloadImage} fetchpriority="high" />
 61→  )}
 62→
 63→  <!-- PWA Manifest -->
 64→  <link rel="manifest" href="/manifest.webmanifest" />
 65→  <meta name="theme-color" content="#0052cc" />
 66→  <meta name="apple-mobile-web-app-capable" content="yes" />
 67→  <meta name="apple-mobile-web-app-status-bar-style" content="black-translucent" />
 68→  <meta name="apple-mobile-web-app-title" content="GelChile" />
 69→  <link rel="apple-touch-icon" href="/icon-192.svg" />
 70→
 71→  <!-- Inter font: self-hosted via @fontsource-variable/inter, preloaded above -->
 72→
 73→  <!-- DNS prefetch for form APIs (low-cost hint for potential navigation to /cotizacion) -->
 74→  <link rel="dns-prefetch" href="https://api.web3forms.com" />
 75→
 76→  <!-- Cloudflare Turnstile (anti-spam) — solo en páginas con formulario -->
 77→  {includeTurnstile && (
 78→    <link rel="preconnect" href="https://challenges.cloudflare.com" crossorigin />
 79→    <script src="https://challenges.cloudflare.com/turnstile/v0/api.js" async defer></script>
 80→  )}
 81→
 82→  <!-- Anti-flash dark mode script -->
 83→  <script is:inline>
 84→    (function() {
 85→      var t = localStorage.getItem('gelchile_theme') ||
 86→        (window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light');
 87→      document.documentElement.setAttribute('data-theme', t);
 88→    })();
 89→  </script>
 90→
 91→  <!-- Structured Data -->
 92→  <script type="application/ld+json" set:html={JSON.stringify({
 93→    "@context": "https://schema.org",
 94→    "@type": "LocalBusiness",
 95→    "name": siteConfig.name,
 96→    "description": siteConfig.description,
 97→    "url": siteConfig.url,
 98→    "telephone": siteConfig.contact.phones[0].replace(/\s/g, ''),
 99→    "email": siteConfig.contact.email,
100→    "address": {
101→      "@type": "PostalAddress",
102→      "streetAddress": "Av. Uno #3016",
103→      "addressLocality": "Cerrillos",
104→      "addressRegion": "RM",
105→      "addressCountry": "CL"
106→    }
107→  })} />
108→</head>
109→<body>
110→  <!-- H1: Barra de progreso de navegación -->
111→  <div id="page-progress" class="page-progress"></div>
112→
113→  <a href="#main-content" class="skip-link">Saltar al contenido principal</a>
114→
115→  <Header currentPath={Astro.url.pathname} />
116→
117→  <main id="main-content">
118→    <slot />
119→  </main>
120→
121→  <Footer />
122→
123→  <Toast client:visible />
124→  <DarkModeToggle client:visible />
125→  <InstallPrompt />
126→
127→  <!-- Scroll to Top Button -->
128→  <button id="scroll-top-btn" class="scroll-top-btn" aria-label="Volver arriba">
129→    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" width="24" height="24" aria-hidden="true">
130→      <polyline points="18 15 12 9 6 15"/>
131→    </svg>
132→  </button>
133→
134→  <!-- Consolidated inline scripts: AOS, SW, mobile menu, progress bar, scroll-to-top -->
135→  <script is:inline>
136→    // AOS: Animate On Scroll via IntersectionObserver
137→    requestAnimationFrame(function() {
138→      var els = document.querySelectorAll('[data-aos]');
139→      if (!els.length || !('IntersectionObserver' in window)) {
140→        els.forEach(function(el) { el.classList.add('aos-animate'); });
141→        return;
142→      }
143→      var observer = new IntersectionObserver(function(entries) {
144→        entries.forEach(function(entry) {
145→          if (entry.isIntersecting) {
146→            entry.target.classList.add('aos-animate');
147→            observer.unobserve(entry.target);
148→          }
149→        });
150→      }, { threshold: 0.1 });
151→      els.forEach(function(el) { observer.observe(el); });
152→    });
153→
154→    // Service Worker Registration
155→    if ('serviceWorker' in navigator) {
156→      window.addEventListener('load', function() {
157→        navigator.serviceWorker.register('/sw.js');
158→      });
159→    }
160→
161→    // Mobile Menu
162→    (function() {
163→      var menuBtn = document.querySelector('.mobile-menu-btn');
164→      var menu = document.querySelector('.mobile-menu');
165→      if (!menuBtn || !menu) return;
166→      var isOpen = false;
167→      function openMenu() {
168→        isOpen = true;
169→        menu.classList.add('active');
170→        menuBtn.classList.add('active');
171→        menuBtn.setAttribute('aria-expanded', 'true');
172→        document.body.style.overflow = 'hidden';
173→      }
174→      function closeMenu() {
175→        isOpen = false;
176→        menu.classList.remove('active');
177→        menuBtn.classList.remove('active');
178→        menuBtn.setAttribute('aria-expanded', 'false');
179→        document.body.style.overflow = '';
180→      }
181→      menuBtn.addEventListener('click', function(e) {
182→        e.stopPropagation();
183→        isOpen ? closeMenu() : openMenu();
184→      });
185→      document.addEventListener('click', function(e) {
186→        if (isOpen && !menu.contains(e.target) && !menuBtn.contains(e.target)) closeMenu();
187→      });
188→      document.addEventListener('keydown', function(e) {
189→        if (e.key === 'Escape' && isOpen) closeMenu();
190→      });
191→      menu.querySelectorAll('.nav-link').forEach(function(link) {
192→        link.addEventListener('click', closeMenu);
193→      });
194→    })();
195→
196→    // Page transition progress bar
197→    (function() {
198→      var bar = document.getElementById('page-progress');
199→      if (!bar) return;
200→      document.addEventListener('click', function(e) {
201→        var link = e.target.closest('a');
202→        if (link && link.href && link.href.startsWith(window.location.origin) && !link.hasAttribute('download')) {
203→          bar.style.transform = 'scaleX(0.3)';
204→        }
205→      });
206→      window.addEventListener('load', function() {
207→        bar.style.transform = 'scaleX(1)';
208→        setTimeout(function() { bar.style.transform = 'scaleX(0)'; }, 300);
209→      });
210→    })();
211→
212→    // Scroll to Top
213→    (function() {
214→      var btn = document.getElementById('scroll-top-btn');
215→      if (!btn) return;
216→      var visible = false;
217→      var threshold = 400;
218→      var onScroll = function() {
219→        var show = window.scrollY > threshold;
220→        if (show !== visible) {
221→          visible = show;
222→          btn.classList.toggle('visible', show);
223→        }
224→      };
225→      window.addEventListener('scroll', onScroll, { passive: true });
226→      btn.addEventListener('click', function() {
227→        window.scrollTo({ top: 0, behavior: 'smooth' });
228→      });
229→    })();
230→  </script>
231→
232→  <style is:global>
233→    /* Scroll to Top Button */
234→    .scroll-top-btn {
235→      position: fixed;
236→      bottom: 120px;
237→      right: 24px;
238→      width: 48px;
239→      height: 48px;
240→      border-radius: 50%;
241→      border: none;
242→      background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-dark) 100%);
243→      color: #fff;
244→      cursor: pointer;
245→      display: flex;
246→      align-items: center;
247→      justify-content: center;
248→      box-shadow: 0 4px 14px rgba(0, 82, 204, 0.35);
249→      opacity: 0;
250→      transform: translateY(16px);
251→      pointer-events: none;
252→      transition: opacity 0.3s ease, transform 0.3s ease, background 0.2s ease;
253→      z-index: 50;
254→    }
255→
256→    .scroll-top-btn.visible {
257→      opacity: 1;
258→      transform: translateY(0);
259→      pointer-events: auto;
260→    }
261→
262→    .scroll-top-btn:hover {
263→      background: linear-gradient(135deg, var(--color-primary-dark) 0%, var(--color-primary) 100%);
264→      box-shadow: 0 6px 20px rgba(0, 82, 204, 0.45);
265→    }
266→
267→    .scroll-top-btn:active {
268→      transform: translateY(0) scale(0.95);
269→    }
270→
271→    .scroll-top-btn svg {
272→      width: 24px;
273→      height: 24px;
274→    }
275→
276→    /* Dark mode */
277→    [data-theme="dark"] .scroll-top-btn {
278→      background: linear-gradient(135deg, #1e40af 0%, #1e3a8a 100%);
279→      box-shadow: 0 4px 14px rgba(30, 64, 175, 0.4);
280→    }
281→
282→    [data-theme="dark"] .scroll-top-btn:hover {
283→      background: linear-gradient(135deg, #1e3a8a 0%, #1e40af 100%);
284→      box-shadow: 0 6px 20px rgba(30, 64, 175, 0.5);
285→    }
286→
287→    /* Responsive: avoid overlap with DarkModeToggle */
288→    @media (max-width: 480px) {
289→      .scroll-top-btn {
290→        bottom: 140px;
291→        right: 16px;
292→        width: 44px;
293→        height: 44px;
294→      }
295→    }
296→
297→    /* H1: Page progress bar */
298→    .page-progress {
299→      position: fixed;
300→      top: 0;
301→      left: 0;
302→      height: 3px;
303→      background: linear-gradient(90deg, var(--color-primary-500), var(--color-accent));
304→      width: 100%;
305→      z-index: 9999;
306→      transform: scaleX(0);
307→      transform-origin: left;
308→      transition: transform 0.3s ease;
309→      pointer-events: none;
310→    }
311→  </style>
312→</body>
313→</html>
314→

  1→---
  2→import AddToQuoteBtn from './islands/AddToQuoteBtn.tsx';
  3→import { slugify } from '../utils/slugify';
  4→import { applicationTags } from '../data/application-tags';
  5→import { getCategoryPlaceholderSvg } from '../utils/category-placeholders';
  6→
  7→interface Props {
  8→  sku: string;
  9→  title: string;
 10→  category: string;
 11→  categorySlug: string;
 12→  description: string;
 13→  image: string | null;
 14→  badge: string | null;
 15→  inStock: boolean;
 16→  delay?: number;
 17→  hasVariants?: boolean;
 18→}
 19→
 20→const { sku, title, category, categorySlug, description, image, badge, inStock, delay, hasVariants } = Astro.props;
 21→const productUrl = `/productos/${slugify(title)}`;
 22→const tags = applicationTags[categorySlug] || [];
 23→---
 24→
 25→<article class="product-card" data-aos="fade-up" data-aos-delay={delay ? String(delay) : undefined}>
 26→  <a href={productUrl} class="product-link">
 27→    <div class="product-image">
 28→      {badge && <span class:list={['product-badge', { 'badge-popular': badge === 'Popular', 'badge-pro': badge === 'Pro' }]}>{badge}</span>}
 29→      {image ? (
 30→        <img src={image} alt={title} loading="lazy" decoding="async" width="300" height="200" sizes="(max-width: 480px) calc(100vw - 32px), (max-width: 768px) calc(50vw - 24px), (max-width: 1024px) calc(33.33vw - 24px), 280px" />
 31→      ) : (
 32→        <Fragment set:html={getCategoryPlaceholderSvg(categorySlug)} />
 33→      )}
 34→    </div>
 35→    <div class="product-content">
 36→      <div class="product-meta-row">
 37→        <span class="product-category">{category}</span>
 38→        {hasVariants && (
 39→          <span class="variant-indicator">
 40→            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" width="11" height="11" aria-hidden="true">
 41→              <rect x="3" y="3" width="7" height="7" rx="1"/>
 42→              <rect x="14" y="3" width="7" height="7" rx="1"/>
 43→              <rect x="3" y="14" width="7" height="7" rx="1"/>
 44→              <rect x="14" y="14" width="7" height="7" rx="1"/>
 45→            </svg>
 46→            Variantes
 47→          </span>
 48→        )}
 49→      </div>
 50→      {tags.length > 0 && (
 51→        <div class="application-tags">
 52→          {tags.map((tag) => (
 53→            <span class="app-tag">{tag}</span>
 54→          ))}
 55→        </div>
 56→      )}
 57→      <h3 class="product-title">{title}</h3>
 58→      <p class="product-description">{description}</p>
 59→      <div class="product-sku-row">
 60→        <span class="product-sku">SKU: {sku}</span>
 61→        <span class:list={['card-stock', { 'out-stock': !inStock }]}>
 62→          <span class="stock-dot"></span>
 63→          {inStock ? 'En Stock' : 'Sin Stock'}
 64→        </span>
 65→      </div>
 66→    </div>
 67→  </a>
 68→  <div class="product-footer">
 69→    <AddToQuoteBtn client:visible sku={sku} title={title} category={category} image={image} inStock={inStock} />
 70→  </div>
 71→</article>
 72→
 73→<style>
 74→  .product-card {
 75→    position: relative;
 76→    background: var(--color-surface-card);
 77→    border-radius: var(--radius-xl);
 78→    overflow: hidden;
 79→    transition: transform 0.25s ease, box-shadow 0.25s ease;
 80→    box-shadow: var(--shadow-card);
 81→    contain: layout style paint;
 82→  }
 83→
 84→  .product-card:hover {
 85→    transform: translateY(-6px);
 86→    box-shadow: var(--shadow-card-hover);
 87→  }
 88→
 89→  .product-link {
 90→    display: block;
 91→    color: inherit;
 92→  }
 93→
 94→  .product-image {
 95→    position: relative;
 96→    height: 220px;
 97→    aspect-ratio: 3/2;
 98→    background: linear-gradient(135deg, rgba(0, 82, 204, 0.04) 0%, rgba(0, 82, 204, 0.01) 100%);
 99→    display: flex;
100→    align-items: center;
101→    justify-content: center;
102→    overflow: hidden;
103→    padding: var(--spacing-4);
104→  }
105→
106→  .product-image img {
107→    width: 100%;
108→    height: 100%;
109→    object-fit: contain;
110→    transition: transform 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
111→  }
112→
113→  .product-card:hover .product-image img {
114→    transform: scale(1.08);
115→  }
116→
117→  .product-image :global(svg) {
118→    width: 120px;
119→    height: 120px;
120→    color: var(--color-gray-400);
121→  }
122→
123→  .product-badge {
124→    position: absolute;
125→    top: var(--spacing-3);
126→    left: var(--spacing-3);
127→    padding: var(--spacing-1) var(--spacing-3);
128→    background: var(--color-primary);
129→    color: var(--color-text-on-primary);
130→    font-size: var(--font-size-xs);
131→    font-weight: var(--font-weight-semibold);
132→    border-radius: var(--radius-full);
133→    text-transform: uppercase;
134→    letter-spacing: 0.5px;
135→    z-index: 2;
136→    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
137→  }
138→
139→  .badge-popular {
140→    background: linear-gradient(135deg, #FF6B00 0%, #FF8C38 100%);
141→  }
142→
143→  .badge-pro {
144→    background: linear-gradient(135deg, #0052CC 0%, #2684FF 100%);
145→  }
146→
147→  .product-content {
148→    padding: var(--spacing-5) var(--spacing-6);
149→  }
150→
151→  /* Meta row: category + variant indicator */
152→  .product-meta-row {
153→    display: flex;
154→    align-items: center;
155→    gap: var(--spacing-2);
156→    margin-bottom: var(--spacing-2);
157→    flex-wrap: wrap;
158→  }
159→
160→  .product-category {
161→    display: inline-block;
162→    padding: var(--spacing-1) var(--spacing-2);
163→    background: var(--color-primary-50);
164→    color: var(--color-primary);
165→    font-size: var(--font-size-xs);
166→    font-weight: var(--font-weight-medium);
167→    border-radius: var(--radius-sm);
168→  }
169→
170→  .variant-indicator {
171→    display: inline-flex;
172→    align-items: center;
173→    gap: 3px;
174→    padding: var(--spacing-1) var(--spacing-2);
175→    background: var(--color-accent, #FF6B00);
176→    color: #fff;
177→    font-size: 11px;
178→    font-weight: var(--font-weight-semibold);
179→    border-radius: var(--radius-sm);
180→  }
181→
182→  /* Application tags */
183→  .application-tags {
184→    display: flex;
185→    gap: var(--spacing-1);
186→    margin-bottom: var(--spacing-2);
187→    flex-wrap: wrap;
188→  }
189→
190→  .app-tag {
191→    display: inline-block;
192→    padding: 1px 6px;
193→    background: var(--color-gray-100);
194→    color: var(--color-gray-600);
195→    font-size: 10px;
196→    font-weight: var(--font-weight-medium);
197→    border-radius: var(--radius-full);
198→    text-transform: uppercase;
199→    letter-spacing: 0.3px;
200→  }
201→
202→  .product-title {
203→    font-size: var(--font-size-lg);
204→    font-weight: var(--font-weight-semibold);
205→    color: var(--color-gray-900);
206→    margin-bottom: var(--spacing-2);
207→    line-height: 1.3;
208→  }
209→
210→  .product-description {
211→    font-size: var(--font-size-sm);
212→    color: var(--color-gray-500);
213→    margin-bottom: var(--spacing-3);
214→    display: -webkit-box;
215→    -webkit-line-clamp: 2;
216→    -webkit-box-orient: vertical;
217→    overflow: hidden;
218→  }
219→
220→  /* SKU + Stock row */
221→  .product-sku-row {
222→    display: flex;
223→    align-items: center;
224→    justify-content: space-between;
225→    margin-bottom: var(--spacing-4);
226→  }
227→
228→  .product-sku {
229→    font-size: var(--font-size-xs);
230→    color: var(--color-gray-400);
231→  }
232→
233→  .card-stock {
234→    display: inline-flex;
235→    align-items: center;
236→    gap: 4px;
237→    font-size: 11px;
238→    font-weight: var(--font-weight-medium);
239→    color: var(--color-success, #36B37E);
240→  }
241→
242→  .card-stock.out-stock {
243→    color: var(--color-gray-400);
244→  }
245→
246→  .stock-dot {
247→    width: 6px;
248→    height: 6px;
249→    border-radius: 50%;
250→    background: currentColor;
251→    animation: pulse 2s ease-in-out infinite;
252→  }
253→
254→  .card-stock.out-stock .stock-dot {
255→    animation: none;
256→  }
257→
258→  .product-footer {
259→    display: flex;
260→    align-items: center;
261→    justify-content: stretch;
262→    padding: 0 var(--spacing-6) var(--spacing-6);
263→    padding-top: var(--spacing-4);
264→    border-top: 1px solid var(--color-gray-100);
265→    margin: 0 var(--spacing-6);
266→    padding-left: 0;
267→    padding-right: 0;
268→    margin-bottom: var(--spacing-6);
269→  }
270→
271→  .btn-add-quote {
272→    display: flex;
273→    align-items: center;
274→    justify-content: center;
275→    gap: var(--spacing-2);
276→    width: 100%;
277→    padding: var(--spacing-3) var(--spacing-4);
278→    background: linear-gradient(135deg, #C45000 0%, #E86100 100%);
279→    color: var(--color-white);
