120→
121→  /* Sombras */
122→  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
123→  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
124→  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
125→  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
126→  --shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
127→  --shadow-card: 0 2px 8px rgba(0, 82, 204, 0.08);
128→  --shadow-card-hover: 0 12px 24px rgba(0, 82, 204, 0.15);
129→
130→  /* Transiciones */
131→  --transition-fast: 150ms ease;
132→  --transition-base: 300ms ease;
133→  --transition-slow: 500ms ease;
134→  --transition-bounce: 500ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
135→
136→  /* Z-index */
137→  --z-dropdown: 100;
138→  --z-sticky: 200;
139→  --z-fixed: 300;
140→  --z-modal-backdrop: 400;
141→  --z-modal: 500;
142→  --z-popover: 600;
143→  --z-tooltip: 700;
144→
145→  /* Contenedor */
146→  --container-max: 1280px;
147→  --container-padding: var(--spacing-6);
148→}
