26→/** Cart count badge with bounce animation. Mount with client:load inside Header. */
27→export default function CartCount() {
28→  const count = useStore($cartCount);
29→  const [animate, setAnimate] = useState(false);
30→  const prevCount = useRef(count);
31→
32→  useEffect(() => {
33→    initCart();
34→  }, []);
35→
36→  // Bounce animation when count increases
37→  useEffect(() => {
38→    if (count > prevCount.current) {
39→      setAnimate(true);
40→      const timer = setTimeout(() => setAnimate(false), 300);
41→      return () => clearTimeout(timer);
42→    }
43→    prevCount.current = count;
44→  }, [count]);
45→
46→  if (count === 0) return null;
47→
48→  return <span style={animate ? bounceStyle : badgeStyle} aria-label={`${count} articulo${count !== 1 ? 's' : ''} en cotizacion`}>{count}</span>;
49→}
