 26→/** Product filter + search + grid. Mount with client:load on /productos. */
 27→export default function ProductFilter({ products, categories }: Props) {
 28→  const [selectedCategory, setSelectedCategory] = useState<string>('todos');
 29→  const [selectedSubcategory, setSelectedSubcategory] = useState<string>('todos');
 30→  const [searchText, setSearchText] = useState('');
 31→  const [inputValue, setInputValue] = useState('');
 32→  const [sortBy, setSortBy] = useState<SortOption>('default');
 33→  const cartSkuMap = useStore($cartSkuMap);
 34→  const searchInputRef = useRef<HTMLInputElement>(null);
 35→  const debounceRef = useRef<ReturnType<typeof setTimeout>>();
 36→
 37→  const handleSearchInput = (value: string) => {
 38→    setInputValue(value);
 39→    clearTimeout(debounceRef.current);
 40→    debounceRef.current = setTimeout(() => setSearchText(value), 200);
 41→  };
 42→
 43→  // Read initial category from URL on mount
 44→  useEffect(() => {
 45→    if (typeof window === 'undefined') return;
 46→    const params = new URLSearchParams(window.location.search);
 47→    const cat = params.get('categoria');
 48→    if (cat && categories.some((c) => c.id === cat)) {
 49→      setSelectedCategory(cat);
 50→    }
 51→  }, []);
 52→
 53→  // Update URL when filter changes
 54→  useEffect(() => {
 55→    if (typeof window === 'undefined') return;
 56→    const url = new URL(window.location.href);
 57→    if (selectedCategory === 'todos') {
 58→      url.searchParams.delete('categoria');
 59→    } else {
 60→      url.searchParams.set('categoria', selectedCategory);
 61→    }
 62→    window.history.replaceState({}, '', url.toString());
 63→  }, [selectedCategory]);
 64→
 65→  // H7: Cmd+K / Ctrl+K keyboard shortcut to focus search
 66→  useEffect(() => {
 67→    if (typeof window === 'undefined') return;
 68→    const handleKeyDown = (e: KeyboardEvent) => {
 69→      // Cmd+K on Mac, Ctrl+K on Windows/Linux
 70→      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
 71→        e.preventDefault();
 72→        searchInputRef.current?.focus();
 73→        searchInputRef.current?.select();
 74→      }
 75→      // Escape to blur search and clear
 76→      if (e.key === 'Escape' && document.activeElement === searchInputRef.current) {
 77→        searchInputRef.current?.blur();
 78→        if (inputValue) {
 79→          clearTimeout(debounceRef.current);
 80→          setInputValue('');
 81→          setSearchText('');
 82→        }
 83→      }
 84→    };
 85→    document.addEventListener('keydown', handleKeyDown);
 86→    return () => document.removeEventListener('keydown', handleKeyDown);
 87→  }, [inputValue]);
 88→
 89→  // Filter products (memoized)
 90→  const query = searchText.toLowerCase().trim();
 91→  const filtered = useMemo(() => products.filter((p) => {
 92→    const matchCategory = selectedCategory === 'todos' || p.categorySlug === selectedCategory;
 93→    const matchSubcategory = selectedSubcategory === 'todos' || (p.subcategories?.includes(selectedSubcategory) ?? false);
 94→    const matchSearch = !query ||
 95→      p.title.toLowerCase().includes(query) ||
 96→      p.sku.toLowerCase().includes(query) ||
 97→      p.description.toLowerCase().includes(query);
 98→    return matchCategory && matchSubcategory && matchSearch;
 99→  }), [products, selectedCategory, selectedSubcategory, query]);
