  1→/**
  2→ * Gel Chile - Service Worker for PWA
  3→ * Stale-while-revalidate for assets, network-first for HTML
  4→ */
  5→
  6→// BUILD_TIMESTAMP is replaced by the build script for cache busting
  7→const CACHE_NAME = 'gelchile-1771133709729';
  8→const OFFLINE_URL = '/offline.html';
  9→
 10→// Assets to cache on install
 11→const PRECACHE_ASSETS = [
 12→  '/',
 13→  '/productos',
 14→  '/nosotros',
 15→  '/cotizacion',
 16→  '/assets/img/gelchile-logo.webp',
 17→  '/favicon.svg',
 18→  '/icon-192.svg',
 19→  '/icon-512.svg',
 20→];
 21→
 22→// Install: precache essential assets
 23→self.addEventListener('install', (event) => {
 24→  event.waitUntil(
 25→    caches.open(CACHE_NAME).then((cache) => {
 26→      return cache.addAll(PRECACHE_ASSETS);
 27→    })
 28→  );
 29→  self.skipWaiting();
 30→});
 31→
 32→// Activate: clean old caches
 33→self.addEventListener('activate', (event) => {
 34→  event.waitUntil(
 35→    caches.keys().then((cacheNames) => {
 36→      return Promise.all(
 37→        cacheNames
 38→          .filter((name) => name !== CACHE_NAME)
 39→          .map((name) => caches.delete(name))
 40→      );
 41→    })
 42→  );
 43→  self.clients.claim();
 44→});
 45→
 46→// Helpers
 47→function isAsset(url) {
 48→  return url.pathname.startsWith('/_astro/') ||
 49→         url.pathname.startsWith('/assets/') ||
 50→         url.pathname.match(/\.(webp|png|jpg|svg|woff2|css|js)$/);
 51→}
 52→
 53→// Fetch handler with split strategy
 54→self.addEventListener('fetch', (event) => {
 55→  // Skip non-GET requests
 56→  if (event.request.method !== 'GET') return;
 57→
 58→  // Skip external requests
 59→  if (!event.request.url.startsWith(self.location.origin)) return;
 60→
 61→  // Skip API requests and form submissions
 62→  if (event.request.url.includes('/api/') ||
 63→      event.request.url.includes('web3forms.com')) {
 64→    return;
 65→  }
 66→
 67→  const url = new URL(event.request.url);
 68→
 69→  if (isAsset(url)) {
 70→    // Stale-while-revalidate for static assets
 71→    event.respondWith(
 72→      caches.match(event.request).then((cached) => {
 73→        const fetchPromise = fetch(event.request).then((response) => {
 74→          if (response.status === 200) {
 75→            const clone = response.clone();
 76→            caches.open(CACHE_NAME).then((cache) => cache.put(event.request, clone));
 77→          }
 78→          return response;
 79→        }).catch(() => cached);
 80→
 81→        return cached || fetchPromise;
 82→      })
 83→    );
 84→  } else {
 85→    // Network-first for HTML pages
 86→    event.respondWith(
 87→      fetch(event.request)
 88→        .then((response) => {
 89→          if (response.status === 200) {
 90→            const clone = response.clone();
 91→            caches.open(CACHE_NAME).then((cache) => cache.put(event.request, clone));
 92→          }
 93→          return response;
 94→        })
 95→        .catch(() => {
 96→          return caches.match(event.request).then((cached) => {
 97→            if (cached) return cached;
 98→            if (event.request.headers.get('Accept')?.includes('text/html')) {
 99→              return caches.match(OFFLINE_URL);
100→            }
101→            return new Response('', { status: 503, statusText: 'Offline' });
102→          });
103→        })
104→    );
105→  }
106→});
