 16→export async function getStaticPaths() {
 17→  const products = await getCollection('products');
 18→  return products.map(product => {
 19→    const sameCategory = products.filter(
 20→      p => p.data.categorySlug === product.data.categorySlug && p.data.sku !== product.data.sku
 21→    );
 22→    const sorted = [...sameCategory].sort((a, b) => a.data.sku.localeCompare(b.data.sku));
 23→    let related = sorted.slice(0, 4);
 24→
 25→    if (related.length < 3) {
 26→      const otherCategory = products.filter(
 27→        p => p.data.categorySlug !== product.data.categorySlug && p.data.sku !== product.data.sku
 28→      );
 29→      const otherSorted = [...otherCategory].sort((a, b) => a.data.sku.localeCompare(b.data.sku));
 30→      related = [...related, ...otherSorted.slice(0, 4 - related.length)];
 31→    }
 32→
 33→    // Complementary products from related categories
 34→    const compCategorySlugs = complementaryCategories[product.data.categorySlug] || [];
 35→    const complementary = products
 36→      .filter(p => compCategorySlugs.includes(p.data.categorySlug) && p.data.sku !== product.data.sku)
 37→      .sort((a, b) => a.data.sku.localeCompare(b.data.sku))
 38→      .slice(0, 4);
 39→
 40→    return {
 41→      params: { slug: slugify(product.data.title) },
 42→      props: {
 43→        product: product.data,
 44→        relatedProducts: related.map(p => p.data),
 45→        complementaryProducts: complementary.map(p => p.data),
 46→      },
 47→    };
 48→  });
 49→}
