 1→import { useStore } from '@nanostores/preact';
 2→import { useState, useEffect, useRef } from 'preact/hooks';
 3→import { $toasts, dismissToast } from '../../stores/toast';
 4→import type { ToastMessage } from '../../stores/toast';
 5→import { siteConfig } from '../../data/site-config';
 6→import styles from './Toast.module.css';
 7→
 8→/** Individual toast with slide-in animation and auto-dismiss. */
 9→function ToastItem({ toast }: { toast: ToastMessage }) {
10→  const [visible, setVisible] = useState(false);
11→  const timerRef = useRef<number | null>(null);
12→
13→  // Longer duration for toasts with actions (5s vs 3s)
14→  const duration = toast.action ? 5000 : siteConfig.ui.toastDuration;
15→
16→  useEffect(() => {
17→    // Slide-in animation on next frame
18→    requestAnimationFrame(() => {
19→      requestAnimationFrame(() => setVisible(true));
20→    });
21→
22→    // Auto-dismiss after duration
23→    timerRef.current = window.setTimeout(() => {
24→      setVisible(false);
25→      // Wait for CSS transition to complete before removing
26→      setTimeout(() => dismissToast(toast.id), 300);
27→    }, duration);
28→
29→    return () => {
30→      if (timerRef.current !== null) clearTimeout(timerRef.current);
31→    };
32→  }, [toast.id, duration]);
33→
34→  const handleClose = () => {
35→    if (timerRef.current !== null) clearTimeout(timerRef.current);
36→    setVisible(false);
37→    setTimeout(() => dismissToast(toast.id), 300);
38→  };
39→
40→  const handleAction = () => {
41→    if (toast.action?.onClick) {
42→      toast.action.onClick();
43→    }
44→    handleClose();
45→  };
46→
47→  const typeClass = toast.type === 'success'
48→    ? styles['toast-success']
49→    : toast.type === 'error'
50→      ? styles['toast-error']

