 48→/* ---- Detection & Parsing ---- */
 49→
 50→function detectMode(variants: Props['variants']): ConfigMode {
 51→  if (!variants.some(v => v.group)) return 'flat';
 52→  const groups = [...new Set(variants.map(v => v.group).filter(Boolean))] as string[];
 53→  if (groups.length <= 1) return 'flat';
 54→  const hasSep = groups.some(g => g.includes(' \u2014 '));
 55→  if (hasSep) {
 56→    const models = [...new Set(groups.map(g => { const i = g.indexOf(' \u2014 '); return i > -1 ? g.substring(0, i) : g; }))];
 57→    return models.length === 1 ? 'two-step-bar' : 'three-step';
 58→  }
 59→  return 'two-step-model';
 60→}
 61→
 62→function parseHierarchy(variants: Props['variants'], mode: ConfigMode): ModelData[] {
 63→  if (mode === 'flat') return [];
 64→  const map = new Map<string, ModelData>();
 65→
 66→  if (mode === 'three-step' || mode === 'two-step-bar') {
 67→    for (const v of variants) {
 68→      const g = v.group || '';
 69→      const sep = g.indexOf(' \u2014 ');
 70→      const modelCode = sep > -1 ? g.substring(0, sep) : g;
 71→      const barSize = sep > -1 ? g.substring(sep + 3) : '';
 72→      if (!map.has(modelCode)) map.set(modelCode, { code: modelCode, bars: [] });
 73→      const model = map.get(modelCode)!;
 74→      let bar = model.bars!.find(b => b.size === barSize);
 75→      if (!bar) { bar = { size: barSize, cables: [] }; model.bars!.push(bar); }
 76→      bar.cables.push({ id: v.id, label: v.label, group: g });
 77→    }
 78→  } else {
 79→    for (const v of variants) {
 80→      const code = v.group || '';
 81→      if (!map.has(code)) map.set(code, { code, cables: [] });
 82→      map.get(code)!.cables!.push({ id: v.id, label: v.label, group: code });
 83→    }
 84→  }
 85→
 86→  return [...map.values()];
 87→}
