117→function FlatSelector({ sku, title, category, image, variants, onAdd }: Props) {
118→  const cart = useStore($cart);
119→
120→  const [state, setState] = useState<Record<string, VariantState>>(() => {
121→    const initial: Record<string, VariantState> = {};
122→    for (const v of variants) initial[v.id] = { selected: false, quantity: 1 };
123→    return initial;
124→  });
125→
126→  const selectedCount = Object.values(state).filter(s => s.selected).length;
127→
128→  const toggleVariant = (id: string) => {
129→    setState(prev => ({ ...prev, [id]: { ...prev[id], selected: !prev[id].selected } }));
130→  };
131→
132→  const setQuantity = (id: string, qty: number) => {
133→    const q = Math.max(1, Math.min(99, qty));
134→    setState(prev => ({ ...prev, [id]: { ...prev[id], quantity: q, selected: true } }));
135→  };
136→
137→  const handleAdd = () => {
138→    const toAdd = variants.filter(v => state[v.id].selected);
139→    if (toAdd.length === 0) return;
140→    for (const v of toAdd) {
141→      addItem({
142→        sku, title, category, quantity: state[v.id].quantity, image,
143→        variant: v.group ? `${v.group} \u2014 ${v.label}` : v.label,
144→      });
145→    }
146→    const msg = toAdd.length === 1
147→      ? `${toAdd[0].label} agregado a la cotización`
148→      : `${toAdd.length} variantes de ${title} agregadas a la cotización`;
149→    showToast(msg, 'success');
150→    setState(prev => {
151→      const reset: Record<string, VariantState> = {};
152→      for (const key of Object.keys(prev)) reset[key] = { selected: false, quantity: 1 };
153→      return reset;
154→    });
155→    onAdd?.();
156→  };
157→
158→  const getCartQty = (v: { label: string; group?: string }): number => {
159→    const variantLabel = v.group ? `${v.group} \u2014 ${v.label}` : v.label;
160→    const item = cart.find(i => i.sku === sku && i.variant === variantLabel);
161→    return item ? item.quantity : 0;
162→  };
163→
164→  /* Grouping logic */
165→  const hasGroups = variants.some(v => v.group);
166→  const subtypes = hasGroups
167→    ? [...new Set(variants.map(v => {
168→        const g = v.group || '';
169→        const idx = g.indexOf(' \u2014 ');
170→        return idx > -1 ? g.substring(0, idx) : g;
171→      }))]
172→    : [];
173→  const uniqueGroups = hasGroups ? [...new Set(variants.map(v => v.group).filter(Boolean))] : [];
174→  const isSingleGroup = uniqueGroups.length === 1;
175→  const grouped = hasGroups && !isSingleGroup
176→    ? variants.reduce<Array<{ group: string; items: typeof variants }>>((acc, v) => {
177→        const g = v.group || '';
178→        const existing = acc.find(entry => entry.group === g);
179→        if (existing) existing.items.push(v);
180→        else acc.push({ group: g, items: [v] });
181→        return acc;
182→      }, [])
183→    : null;
184→
185→  const renderVariantRow = (v: (typeof variants)[0]) => {
186→    const s = state[v.id];
187→    const cartQty = getCartQty(v);
188→    return (
189→      <label key={v.id} class={`${styles.row} ${s.selected ? styles.selected : ''}`}>
190→        <input type="checkbox" class={styles.checkbox} checked={s.selected} onChange={() => toggleVariant(v.id)} />
191→        <span class={styles.label}>
192→          {v.label}
193→          {cartQty > 0 && <span class={styles.inCart}> (en cotización: {cartQty})</span>}
194→        </span>
195→        <div class={styles.qtyControls}>
196→          <button type="button" class={styles.qtyBtn} onClick={(e) => { e.preventDefault(); setQuantity(v.id, s.quantity - 1); }} aria-label="Disminuir cantidad">{'\u2212'}</button>
197→          <input type="number" class={styles.qtyInput} min="1" max="99" value={s.quantity} onInput={(e) => { const val = parseInt((e.target as HTMLInputElement).value); if (!isNaN(val)) setQuantity(v.id, val); }} />
198→          <button type="button" class={styles.qtyBtn} onClick={(e) => { e.preventDefault(); setQuantity(v.id, s.quantity + 1); }} aria-label="Aumentar cantidad">+</button>
199→        </div>
200→      </label>
201→    );
202→  };
203→
204→  return (
205→    <div class={styles.selector}>
206→      {hasGroups && subtypes.length > 0 && (
207→        <div class={styles.modelInfo}>
208→          <span class={styles.modelLabel}>{subtypes.length === 1 ? 'Modelo' : 'Modelos disponibles'}</span>
209→          <span class={styles.modelName}>
210→            {subtypes.map((name, i) => (
211→              <span key={name}>
212→                {i > 0 && <span class={styles.modelSeparator}>/</span>}
213→                {name}
214→              </span>
215→            ))}
216→          </span>
217→        </div>
218→      )}
219→
220→      <div class={styles.selectorHeading}>
221→        <span class={styles.headingText}>Seleccionar variante</span>
222→        <span class={styles.headingCount}>
223→          {selectedCount > 0 ? `${selectedCount} / ${variants.length}` : `${variants.length} opciones`}
224→        </span>
225→      </div>
226→
227→      <div class={styles.list}>
228→        {grouped
229→          ? grouped.map(({ group, items }) => (
230→              <div key={group}>
231→                <div class={styles.groupDivider}>{group}</div>
232→                {items.map(renderVariantRow)}
233→              </div>
234→            ))
235→          : variants.map(renderVariantRow)
236→        }
237→      </div>
238→
239→      <button type="button" class={styles.addBtn} onClick={handleAdd} disabled={selectedCount === 0}>
240→        <CartIcon />
241→        {selectedCount === 0
242→          ? 'Selecciona al menos una variante'
243→          : selectedCount === 1
244→            ? 'Agregar 1 variante a Cotización'
245→            : `Agregar ${selectedCount} variantes a Cotización`}
246→      </button>
247→    </div>
248→  );
249→}
