 80→<script>
 81→  // Count-up animation for hero stats
 82→  const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;
 83→
 84→  if (!prefersReducedMotion) {
 85→    const statsSection = document.querySelector('.hero-stats');
 86→    if (statsSection) {
 87→      const counters = statsSection.querySelectorAll('[data-count-target]');
 88→      const textEl = statsSection.querySelector('[data-count-text]');
 89→      let animated = false;
 90→
 91→      const animateCountUp = () => {
 92→        if (animated) return;
 93→        animated = true;
 94→
 95→        counters.forEach((el) => {
 96→          const target = parseInt(el.getAttribute('data-count-target') || '0', 10);
 97→          const suffix = el.getAttribute('data-count-suffix') || '';
 98→          const duration = 1500;
 99→          const start = performance.now();
100→          el.textContent = '0';
101→
102→          const step = (now: number) => {
103→            const elapsed = now - start;
104→            const progress = Math.min(elapsed / duration, 1);
105→            // Ease-out cubic
106→            const eased = 1 - Math.pow(1 - progress, 3);
107→            const current = Math.round(eased * target);
108→            el.textContent = current + (progress >= 1 ? suffix : '');
109→            if (progress < 1) requestAnimationFrame(step);
110→          };
111→          requestAnimationFrame(step);
112→        });
113→
114→        if (textEl) {
115→          textEl.style.opacity = '0';
116→          textEl.style.transition = 'opacity 0.6s ease 0.8s';
117→          requestAnimationFrame(() => { (textEl as HTMLElement).style.opacity = '1'; });
118→        }
119→      };
120→
121→      const observer = new IntersectionObserver(
122→        (entries) => {
123→          if (entries[0].isIntersecting) {
124→            animateCountUp();
125→            observer.disconnect();
126→          }
127→        },
128→        { threshold: 0.3 }
129→      );
130→      observer.observe(statsSection);
131→    }
132→  }
133→</script>
