  1→#!/usr/bin/env node
  2→/**
  3→ * Image optimization script for GelChile product images.
  4→ * Resizes to max 800px width and converts to WebP format.
  5→ * Also updates product JSON files to reference .webp extensions.
  6→ */
  7→import sharp from 'sharp';
  8→import { readdir, stat, readFile, writeFile, unlink } from 'node:fs/promises';
  9→import { join, extname, basename } from 'node:path';
 10→
 11→const PRODUCTS_IMG_DIR = join(process.cwd(), 'public/assets/img/products');
 12→const PRODUCTS_JSON_DIR = join(process.cwd(), 'src/content/products');
 13→const MAX_WIDTH = 560;
 14→const WEBP_QUALITY = 80;
 15→const VALID_EXTENSIONS = new Set(['.png', '.jpg', '.jpeg']);
 16→
 17→async function optimizeImages() {
 18→  console.log('=== GelChile Image Optimization ===\n');
 19→
 20→  const files = await readdir(PRODUCTS_IMG_DIR);
 21→  const imageFiles = files.filter(f => VALID_EXTENSIONS.has(extname(f).toLowerCase()));
 22→
 23→  console.log(`Found ${imageFiles.length} images to optimize in ${PRODUCTS_IMG_DIR}\n`);
 24→
 25→  let totalOriginal = 0;
 26→  let totalOptimized = 0;
 27→  let processed = 0;
 28→  let skipped = 0;
 29→
 30→  for (const file of imageFiles) {
 31→    const inputPath = join(PRODUCTS_IMG_DIR, file);
 32→    const ext = extname(file);
 33→    const nameWithoutExt = basename(file, ext);
 34→    const outputPath = join(PRODUCTS_IMG_DIR, `${nameWithoutExt}.webp`);
 35→
 36→    try {
 37→      const originalStat = await stat(inputPath);
 38→      const originalSize = originalStat.size;
 39→      totalOriginal += originalSize;
 40→
 41→      const image = sharp(inputPath);
 42→      const metadata = await image.metadata();
 43→
 44→      let pipeline = image;
 45→
 46→      // Only resize if wider than MAX_WIDTH
 47→      if (metadata.width && metadata.width > MAX_WIDTH) {
 48→        pipeline = pipeline.resize(MAX_WIDTH, null, {
 49→          withoutEnlargement: true,
 50→          fit: 'inside',
 51→        });
 52→      }
 53→
 54→      // Convert to WebP
 55→      const outputBuffer = await pipeline
 56→        .webp({ quality: WEBP_QUALITY })
 57→        .toBuffer();
 58→
 59→      await writeFile(outputPath, outputBuffer);
 60→      totalOptimized += outputBuffer.length;
 61→
 62→      const savings = ((1 - outputBuffer.length / originalSize) * 100).toFixed(1);
 63→      const origMB = (originalSize / 1024 / 1024).toFixed(2);
 64→      const optKB = (outputBuffer.length / 1024).toFixed(1);
 65→      console.log(`  ${file} (${origMB}MB) → ${nameWithoutExt}.webp (${optKB}KB) [${savings}% smaller]`);
 66→
 67→      // Remove original if output is different file
 68→      if (outputPath !== inputPath) {
 69→        await unlink(inputPath);
 70→      }
 71→
 72→      processed++;
 73→    } catch (err) {
 74→      console.error(`  ERROR processing ${file}: ${err.message}`);
 75→      skipped++;
 76→    }
 77→  }
 78→
 79→  console.log(`\n--- Image Results ---`);
 80→  console.log(`Processed: ${processed} | Skipped: ${skipped}`);
 81→  console.log(`Original total: ${(totalOriginal / 1024 / 1024).toFixed(1)}MB`);
 82→  console.log(`Optimized total: ${(totalOptimized / 1024 / 1024).toFixed(1)}MB`);
 83→  console.log(`Savings: ${((1 - totalOptimized / totalOriginal) * 100).toFixed(1)}%\n`);
 84→
 85→  // Phase 2: Update JSON references
 86→  await updateJsonReferences();
 87→}
 88→
 89→async function updateJsonReferences() {
 90→  console.log('=== Updating Product JSON References ===\n');
 91→
 92→  const files = await readdir(PRODUCTS_JSON_DIR);
 93→  const jsonFiles = files.filter(f => f.endsWith('.json'));
 94→  let updated = 0;
 95→
 96→  for (const file of jsonFiles) {
 97→    const filePath = join(PRODUCTS_JSON_DIR, file);
 98→    const content = await readFile(filePath, 'utf-8');
 99→    let data = JSON.parse(content);
100→    let changed = false;
101→
102→    // Update main image
103→    if (data.image && typeof data.image === 'string') {
104→      const newPath = replaceExtToWebp(data.image);
105→      if (newPath !== data.image) {
106→        data.image = newPath;
107→        changed = true;
108→      }
109→    }
110→
111→    // Update images array
112→    if (Array.isArray(data.images)) {
113→      data.images = data.images.map(img => {
114→        const newPath = replaceExtToWebp(img);
115→        if (newPath !== img) {
116→          changed = true;
117→          return newPath;
118→        }
119→        return img;
120→      });
121→    }
122→
123→    if (changed) {
124→      await writeFile(filePath, JSON.stringify(data, null, 2) + '\n');
125→      updated++;
126→    }
127→  }
128→
129→  console.log(`Updated ${updated}/${jsonFiles.length} JSON files\n`);
130→}
131→
132→function replaceExtToWebp(path) {
133→  return path.replace(/\.(png|jpg|jpeg)$/i, '.webp');
134→}
135→
136→optimizeImages().catch(err => {
137→  console.error('Fatal error:', err);
138→  process.exit(1);
139→});
