  6→
  7→.filter-container {
  8→  display: flex;
  9→  justify-content: center;
 10→  gap: var(--spacing-3);
 11→  margin-bottom: var(--spacing-8);
 12→  flex-wrap: wrap;
 13→}
 14→
 15→.filter-btn {
 16→  display: flex;
 17→  align-items: center;
 18→  gap: var(--spacing-2);
 19→  padding: var(--spacing-2) var(--spacing-5);
 20→  background: var(--color-white);
 21→  border: 2px solid var(--color-gray-200);
 22→  border-radius: var(--radius-full);
 23→  font-size: var(--font-size-sm);
 24→  font-weight: var(--font-weight-medium);
 25→  color: var(--color-gray-600);
 26→  cursor: pointer;
 27→  transition: background var(--transition-base), border-color var(--transition-base), color var(--transition-base), transform var(--transition-base), box-shadow var(--transition-base);
 28→}
 29→
 30→.filter-btn svg {
 31→  width: 16px;
 32→  height: 16px;
 33→  flex-shrink: 0;
 34→  opacity: 0.7;
 35→  transition: opacity var(--transition-base);
 36→}
 37→
 38→.filter-btn:hover svg,
 39→.filter-btn.active svg {
 40→  opacity: 1;
 41→}
 42→
 43→.filter-btn:hover {
 44→  border-color: var(--color-primary);
 45→  color: var(--color-primary);
 46→  transform: translateY(-2px);
 47→}
 48→
