 52→  color: var(--color-text-on-primary);
 53→  box-shadow: 0 4px 14px rgba(0, 82, 204, 0.3);
 54→}
 55→
 56→/* Subcategory filter pills */
 57→.subcategory-container {
 58→  display: flex;
 59→  justify-content: center;
 60→  gap: var(--spacing-2);
 61→  margin-bottom: var(--spacing-6);
 62→  flex-wrap: wrap;
